import React, { useEffect, useRef, useState } from 'react';
import { Application, Assets, Sprite, Container, TilingSprite } from 'pixi.js';
import './Juego.css';

const Juego = () => {
  // States
  const [loading, setLoading] = useState(true);
  const [modalVisible, setModalVisible] = useState(false);
  const [modalContent, setModalContent] = useState('');
  const [isMobile, setIsMobile] = useState(false);

  // Refs
  const containerRef = useRef(null);
  const appRef = useRef(null);
  const marioRef = useRef(null);
  const worldRef = useRef(null);
  const bloquesRef = useRef([]);
  
  // Movement refs
  const velocity = useRef(0);
  const jumpVelocity = useRef(0);
  const isJumping = useRef(false);
  const direction = useRef('right');

  // Block contents (simplificado)
  const bloqueContenidos = [
    '<h2>Experiencia Profesional</h2><p>Detalles de experiencia...</p>',
    '<h2>Educación</h2><p>Detalles educativos...</p>',
    // ... otros contenidos
  ];

  // Setup game
  useEffect(() => {
    document.body.style.overflow = 'hidden';
    setIsMobile(window.innerWidth <= 768);

    const loadGame = async () => {
      try {
        // Init PixiJS
        const app = new Application();
        await app.init({
          width: window.innerWidth,
          height: window.innerHeight,
          backgroundColor: 0x87ceeb,
          resizeTo: window
        });
        appRef.current = app;
        containerRef.current.appendChild(app.canvas);

        // Load assets
        await Assets.init({ basePath: process.env.PUBLIC_URL + '/assets' });
        await Assets.load([
          'walk0.png', 'walk1.png', 'walk2.png', 'walk3.png',
          'walk4.png', 'walk5.png', 'walk6.png', 'walk7.png',
          'bloque.png', 'suelo.png', 'ciudad.png'
        ].map(src => ({ src })));

        setLoading(false);

        // Get textures
        const textures = {
          walk0: Assets.get('walk0.png'),
          walk1: Assets.get('walk1.png'),
          walk2: Assets.get('walk2.png'),
          walk3: Assets.get('walk3.png'),
          walk4: Assets.get('walk4.png'),
          walk5: Assets.get('walk5.png'),
          walk6: Assets.get('walk6.png'),
          walk7: Assets.get('walk7.png'),
          bloque: Assets.get('bloque.png'),
          suelo: Assets.get('suelo.png'),
          fondo: Assets.get('ciudad.png')
        };

        // Animation frames
        const framesLeft = [textures.walk0, textures.walk1, textures.walk2, textures.walk3];
        const framesRight = [textures.walk7, textures.walk6, textures.walk5, textures.walk4];

        // Setup world
        const fondo = new TilingSprite(textures.fondo, 6000, app.screen.height);
        fondo.zIndex = -1;
        app.stage.addChild(fondo);

        const world = new Container();
        worldRef.current = world;
        app.stage.addChild(world);

        // Ground
        for (let i = 0; i < 100; i++) {
          const suelo = new Sprite(textures.suelo);
          suelo.scale.set(0.4);
          suelo.position.set(i * (suelo.width * 0.3), 750);
          world.addChild(suelo);
        }

        // Blocks
        const bloques = [];
        bloquesRef.current = bloques;
        for (let i = 0; i < 10; i++) {
          const bloque = new Sprite(textures.bloque);
          bloque.scale.set(0.05);
          bloque.position.set(900 + i * 400, 470);
          bloque.eventMode = 'static';
          world.addChild(bloque);
          bloques.push(bloque);
        }

        // Character
        const mario = new Sprite(framesRight[0]);
        mario.scale.set(0.58);
        mario.position.set(app.screen.width / 4, 750);
        mario.anchor.set(0.5, 1);
        marioRef.current = mario;
        world.addChild(mario);

        // Game loop
        app.ticker.add((delta) => {
          // Movement
          const deltaSpeed = velocity.current * 8;
          
          if (velocity.current < 0) { // Left
            if (mario.x > app.screen.width * 0.3) {
              mario.x += deltaSpeed;
            } else {
              world.x -= deltaSpeed;
              fondo.tilePosition.x += deltaSpeed * 0.2;
            }
          } 
          else if (velocity.current > 0) { // Right
            if (mario.x < app.screen.width * 0.7) {
              mario.x += deltaSpeed;
            } else {
              world.x -= deltaSpeed;
              fondo.tilePosition.x += deltaSpeed * 0.2;
            }
          }

          // Jumping
          if (isJumping.current) {
            mario.y += jumpVelocity.current;
            jumpVelocity.current += 1.5;
            
            if (mario.y >= 750) {
              mario.y = 750;
              isJumping.current = false;
              jumpVelocity.current = 0;
            }

            // Block collision
            bloques.forEach((bloque, i) => {
              if (mario.y < bloque.y + 50 && 
                  mario.x + mario.width/2 > bloque.x && 
                  mario.x - mario.width/2 < bloque.x + bloque.width &&
                  jumpVelocity.current < 0) {
                jumpVelocity.current = 10;
                setModalContent(bloqueContenidos[i] || 'Contenido no disponible');
                setModalVisible(true);
              }
            });
          }
        });

        // Controls
        const handleKeyDown = (e) => {
          if (!modalVisible) {
            switch(e.key) {
              case 'ArrowRight':
                velocity.current = 1;
                direction.current = 'right';
                break;
              case 'ArrowLeft':
                velocity.current = -1;
                direction.current = 'left';
                break;
              case 'ArrowUp':
                if (!isJumping.current) {
                  isJumping.current = true;
                  jumpVelocity.current = -25;
                }
                break;
            }
          }
        };

        const handleKeyUp = (e) => {
          if (e.key === 'ArrowRight' || e.key === 'ArrowLeft') {
            velocity.current = 0;
          }
        };

        window.addEventListener('keydown', handleKeyDown);
        window.addEventListener('keyup', handleKeyUp);

        return () => {
          window.removeEventListener('keydown', handleKeyDown);
          window.removeEventListener('keyup', handleKeyUp);
          app.destroy(true);
          document.body.style.overflow = '';
        };
      } catch (err) {
        console.error('Error:', err);
        setModalContent('Error al cargar el juego');
        setModalVisible(true);
        setLoading(false);
      }
    };

    loadGame();

    return () => {
      if (appRef.current) appRef.current.destroy(true);
    };
  }, []);

  return (
    <div className="game-container">
      {loading && <div className="loading">Cargando...</div>}
      <div ref={containerRef} className="pixi-container" />
      
      {modalVisible && (
        <div className="modal">
          <div className="modal-content">
            <span onClick={() => setModalVisible(false)} className="close">&times;</span>
            <div dangerouslySetInnerHTML={{ __html: modalContent }} />
          </div>
        </div>
      )}

      {isMobile && (
        <div className="mobile-controls">
          <button className="mobile-button left">⬅️</button>
          <button className="mobile-button up">⬆️</button>
          <button className="mobile-button right">➡️</button>
        </div>
      )}
    </div>
  );
};

export default Juego;
