import React, { useEffect, useRef, useState } from 'react';
import { Application, Assets, Sprite, Container, TilingSprite, Text, TextStyle } from 'pixi.js';
import './Juego.css';

const Juego = () => {
  // Estados
  const [loading, setLoading] = useState(true);
  const [loadProgress, setLoadProgress] = useState(0);
  const [modalVisible, setModalVisible] = useState(false);
  const [modalContent, setModalContent] = useState('');
  const [muted, setMuted] = useState(false);
  const [isMobile, setIsMobile] = useState(false);

  // Refs
  const containerRef = useRef(null);
  const audioRef = useRef(null);
  const appRef = useRef(null);
  const marioRef = useRef(null);
  const worldRef = useRef(null);
  const fondoRef = useRef(null);
  const bloquesRef = useRef([]);
  
  // Movimiento
  const velocity = useRef(0);
  const jumpVelocity = useRef(0);
  const isJumping = useRef(false);
  const direction = useRef('right');

  // Contenido de los bloques
  const bloqueContenidos = [
    // Experiencia
    `<h2>Experiencia Profesional</h2>
     <h3>GRUPO DCI (Aug 2023 - Actual)</h3>
     <p><strong>Project Manager & Developer</strong></p>
     <ul>
       <li>Gesti贸n de documentaci贸n de proyectos</li>
       <li>Colaboraci贸n con stakeholders</li>
       <li>Desarrollo de sistema de gesti贸n</li>
       <li>Dise帽o de base de datos Firebase</li>
     </ul>`,

    // Educaci贸n
    `<h2>Educaci贸n</h2>
     <h3>Universidad Fidelitas</h3>
     <p><strong>Ingenier铆a en Sistemas</strong> (Dic 2025)</p>`,

    // Habilidades
    `<h2>Habilidades T茅cnicas</h2>
     <div class="skills-columns">
       <div>
         <h3>Lenguajes</h3>
         <ul>
           <li>Dart</li>
           <li>Java</li>
           <li>C#</li>
         </ul>
       </div>
       <div>
         <h3>Frameworks</h3>
         <ul>
           <li>FlutterFlow</li>
           <li>ASP.Net</li>
           <li>React</li>
         </ul>
       </div>
     </div>`,

    // Idiomas
    `<h2>Idiomas</h2>
     <ul>
       <li><strong>Espa帽ol:</strong> Nativo</li>
       <li><strong>Ingl茅s:</strong> Avanzado (B2)</li>
     </ul>`,

    // Contacto
    `<h2>Contacto</h2>
     <ul class="contact-info">
       <li> <strong>Email:</strong> ejemplo@email.com</li>
       <li> <strong>Tel茅fono:</strong> +506 1234 5678</li>
       <li> <strong>LinkedIn:</strong> linkedin.com/in/ejemplo</li>
     </ul>`
  ];

  const etiquetasBloques = ['Experiencia', 'Educaci贸n', 'Habilidades', 'Idiomas', 'Contacto'];

  // Efectos
  useEffect(() => {
    const handleEscape = (e) => {
      if (e.key === 'Escape' && modalVisible) {
        setModalVisible(false);
      }
    };
    window.addEventListener('keydown', handleEscape);
    return () => window.removeEventListener('keydown', handleEscape);
  }, [modalVisible]);

  useEffect(() => {
    const checkMobile = () => setIsMobile(window.innerWidth <= 768);
    checkMobile();
    window.addEventListener('resize', checkMobile);
    return () => window.removeEventListener('resize', checkMobile);
  }, []);

  // Juego principal
  useEffect(() => {
    document.body.style.overflow = 'hidden';

    // Audio
    const setupAudio = () => {
      try {
        audioRef.current = new Audio(`${process.env.PUBLIC_URL}/assets/musica.mp3`);
        audioRef.current.loop = true;
        audioRef.current.volume = 0.05;
        
        const handleFirstInteraction = () => {
          audioRef.current.play().catch(console.warn);
          ['click', 'keydown', 'touchstart'].forEach(evt => 
            document.removeEventListener(evt, handleFirstInteraction)
          );
        };

        ['click', 'keydown', 'touchstart'].forEach(evt => 
          document.addEventListener(evt, handleFirstInteraction)
        );
      } catch (error) {
        console.error('Error en audio:', error);
      }
    };

    setupAudio();

    const initGame = async () => {
      try {
        // Inicializar PixiJS
        const app = new Application();
        await app.init({
          width: window.innerWidth,
          height: window.innerHeight,
          backgroundColor: 0x87ceeb,
          resolution: window.devicePixelRatio || 1,
          resizeTo: window
        });
        appRef.current = app;
        containerRef.current.appendChild(app.canvas);

        // Cargar assets
        await Assets.init({ basePath: `${process.env.PUBLIC_URL}/assets` });
        
        const assetsToLoad = [
          'tile000.png', 'tile001.png', 'tile002.png', 'tile003.png',
          'tile004.png', 'tile005.png', 'tile006.png', 'tile007.png',
          'bloque.png', 'suelo.png', 'ciudad.png', 'nube.png'
        ];

        await Assets.load(assetsToLoad, (progress) => {
          setLoadProgress(Math.round(progress * 100));
        });

        setLoading(false);

        // Configurar texturas
        const textures = {
          walk0: Assets.get('tile000.png'),
          walk1: Assets.get('tile001.png'),
          walk2: Assets.get('tile002.png'),
          walk3: Assets.get('tile003.png'),
          walk4: Assets.get('tile004.png'),
          walk5: Assets.get('tile005.png'),
          walk6: Assets.get('tile006.png'),
          walk7: Assets.get('tile007.png'),
          bloque: Assets.get('bloque.png'),
          suelo: Assets.get('suelo.png'),
          fondo: Assets.get('ciudad.png'),
          nube: Assets.get('nube.png')
        };

        const framesLeft = [textures.walk0, textures.walk1, textures.walk2, textures.walk3];
        const framesRight = [textures.walk7, textures.walk6, textures.walk5, textures.walk4];

        // Fondo
        const fondo = new TilingSprite(textures.fondo, 6000, app.screen.height);
        fondo.zIndex = -1;
        fondoRef.current = fondo;
        app.stage.addChild(fondo);

        // Mundo
        const world = new Container();
        worldRef.current = world;
        app.stage.addChild(world);

        // Nube de bienvenida
        const nubeInicio = new Sprite(textures.nube);
        nubeInicio.position.set(200, 200);
        nubeInicio.scale.set(0.8);
        world.addChild(nubeInicio);

        const bienvenidaText = new Text(
          '隆Bienvenido!\nUsa flechas para moverte\nEscape para cerrar modales',
          new TextStyle({
            fontFamily: 'Arial',
            fontSize: 16,
            fill: 'black',
            align: 'center',
            wordWrap: true,
            wordWrapWidth: 200,
          })
        );
        bienvenidaText.anchor.set(0.5);
        bienvenidaText.position.set(nubeInicio.width / 2, nubeInicio.height / 2);
        nubeInicio.addChild(bienvenidaText);

        // Nubes decorativas
        for (let i = 0; i < 5; i++) {
          const nube = new Sprite(textures.nube);
          nube.position.set(i * 800, 80 + Math.random() * 20);
          nube.scale.set(0.5);
          world.addChild(nube);
        }

        // Suelo
        for (let i = 0; i < 50; i++) {
          const suelo = new Sprite(textures.suelo);
          suelo.scale.set(0.4);
          suelo.position.set(i * (suelo.width * 0.3), 750);
          world.addChild(suelo);
        }

        // Bloques interactivos
        const bloques = [];
        bloquesRef.current = bloques;
        
        for (let i = 0; i < 5; i++) {
          const bloque = new Sprite(textures.bloque);
          bloque.scale.set(0.05);
          bloque.position.set(900 + i * 400, 470);
          bloque.eventMode = 'static';
          bloque.cursor = 'pointer';
          world.addChild(bloque);
          bloques.push(bloque);

          const etiqueta = new Text(etiquetasBloques[i], new TextStyle({
            fontFamily: 'Arial',
            fontSize: 18,
            fill: 'white',
            stroke: '#000',
            strokeThickness: 3,
            align: 'center',
          }));
          etiqueta.anchor.set(0.5);
          etiqueta.position.set(bloque.width / 2, -30);
          bloque.addChild(etiqueta);
        }

        // Personaje
        const mario = new Sprite(framesRight[0]);
        mario.scale.set(0.58);
        mario.position.set(app.screen.width / 4, 750);
        mario.anchor.set(0.5, 1);
        marioRef.current = mario;
        world.addChild(mario);

        // Variables del juego
        const gravity = 1.5;
        const floorY = 750;
        const jumpForce = -25;
        const moveSpeed = 10;
        let currentFrame = 0;
        let frameCounter = 0;

        // Bucle del juego
        app.ticker.add((delta) => {
          // Animaci贸n
          if (velocity.current !== 0) {
            frameCounter += delta;
            if (frameCounter >= 10) {
              frameCounter = 0;
              currentFrame = (currentFrame + 1) % 4;
              mario.texture = direction.current === 'right' 
                ? framesRight[currentFrame] 
                : framesLeft[currentFrame];
            }
          }

          // Movimiento
          const deltaSpeed = velocity.current * moveSpeed * (delta / 16.67);
          
          if (velocity.current < 0) { // Izquierda
            if (mario.x > app.screen.width * 0.3) {
              mario.x += deltaSpeed;
            } else {
              world.x -= deltaSpeed;
              fondo.tilePosition.x += deltaSpeed * 0.2;
            }
          } 
          else if (velocity.current > 0) { // Derecha
            if (mario.x < app.screen.width * 0.7) {
              mario.x += deltaSpeed;
            } else {
              world.x -= deltaSpeed;
              fondo.tilePosition.x += deltaSpeed * 0.2;
            }
          }

          // Salto
          if (isJumping.current) {
            mario.y += jumpVelocity.current * (delta / 16.67);
            jumpVelocity.current += gravity * (delta / 16.67);
            
            if (mario.y >= floorY) {
              mario.y = floorY;
              isJumping.current = false;
              jumpVelocity.current = 0;
            }

            // Colisi贸n con bloques
            bloques.forEach((bloque, index) => {
              if (mario.y < bloque.y + 50 && 
                  mario.y + mario.height > bloque.y &&
                  mario.x + mario.width/2 > bloque.x && 
                  mario.x - mario.width/2 < bloque.x + bloque.width &&
                  jumpVelocity.current < 0) {
                
                jumpVelocity.current = 10;
                setModalContent(bloqueContenidos[index]);
                setModalVisible(true);
                
                // Efecto de rebote del bloque
                const originalY = bloque.y;
                let bounceHeight = 0;
                const bounce = () => {
                  bounceHeight += 2;
                  bloque.y = originalY - bounceHeight;
                  if (bounceHeight >= 15) {
                    app.ticker.remove(bounce);
                    const fall = () => {
                      bounceHeight -= 2;
                      bloque.y = originalY - bounceHeight;
                      if (bounceHeight <= 0) {
                        bloque.y = originalY;
                        app.ticker.remove(fall);
                      }
                    };
                    app.ticker.add(fall);
                  }
                };
                app.ticker.add(bounce);
              }
            });
          }
        });

        // Controles de teclado
        const handleKeyDown = (e) => {
          if (!modalVisible) {
            switch(e.key) {
              case 'ArrowRight':
                velocity.current = 1;
                direction.current = 'right';
                break;
              case 'ArrowLeft':
                velocity.current = -1;
                direction.current = 'left';
                break;
              case 'ArrowUp':
              case ' ':
                if (!isJumping.current && mario.y >= floorY - 1) {
                  isJumping.current = true;
                  jumpVelocity.current = jumpForce;
                }
                break;
            }
          }
        };

        const handleKeyUp = (e) => {
          if (e.key === 'ArrowRight' && velocity.current > 0) {
            velocity.current = 0;
          } else if (e.key === 'ArrowLeft' && velocity.current < 0) {
            velocity.current = 0;
          }
        };

        window.addEventListener('keydown', handleKeyDown);
        window.addEventListener('keyup', handleKeyUp);

        // Controles m贸viles
        const setupMobileControls = () => {
          const leftBtn = document.querySelector('.mobile-button.left');
          const rightBtn = document.querySelector('.mobile-button.right');
          const upBtn = document.querySelector('.mobile-button.up');

          if (leftBtn && rightBtn && upBtn) {
            const handleTouch = (e) => e.preventDefault();
            
            leftBtn.addEventListener('touchstart', (e) => {
              handleTouch(e);
              velocity.current = -1;
              direction.current = 'left';
            });
            
            leftBtn.addEventListener('touchend', handleTouch);
            leftBtn.addEventListener('touchcancel', () => {
              if (velocity.current === -1) velocity.current = 0;
            });

            rightBtn.addEventListener('touchstart', (e) => {
              handleTouch(e);
              velocity.current = 1;
              direction.current = 'right';
            });
            
            rightBtn.addEventListener('touchend', handleTouch);
            rightBtn.addEventListener('touchcancel', () => {
              if (velocity.current === 1) velocity.current = 0;
            });

            upBtn.addEventListener('touchstart', (e) => {
              handleTouch(e);
              if (!isJumping.current) {
                isJumping.current = true;
                jumpVelocity.current = jumpForce;
              }
            });
          }
        };

        if (isMobile) {
          setTimeout(setupMobileControls, 100);
        }

        return () => {
          window.removeEventListener('keydown', handleKeyDown);
          window.removeEventListener('keyup', handleKeyUp);
          app.destroy(true);
          document.body.style.overflow = '';
        };

      } catch (err) {
        console.error('Error en el juego:', err);
        setModalContent(`
          <h2>Error al cargar el juego</h2>
          <p>${err.message}</p>
          <p>Por favor revisa la consola para m谩s detalles.</p>
        `);
        setModalVisible(true);
        setLoading(false);
      }
    };

    initGame();

    return () => {
      if (appRef.current) {
        appRef.current.destroy(true);
      }
      if (audioRef.current) {
        audioRef.current.pause();
        audioRef.current = null;
      }
      document.body.style.overflow = '';
    };
  }, []);

  return (
    <div className="game-container">
      {loading && (
        <div className="loading-screen">
          <div className="loading-progress">
            <div className="progress-bar" style={{ width: `${loadProgress}%` }}></div>
            <div className="progress-text">Cargando... {loadProgress}%</div>
          </div>
        </div>
      )}

      <header className="header-juego">
        <h1>Mi Portafolio Interactivo</h1>
        <div className="redes">
          <button
            className="mute-button"
            onClick={() => {
              if (audioRef.current) {
                audioRef.current.muted = !audioRef.current.muted;
                setMuted(audioRef.current.muted);
              }
            }}
          >
            {muted ? '' : ''}
          </button>
        </div>
      </header>

      <div ref={containerRef} className="pixi-container" />

      {modalVisible && (
        <div className="modal">
          <div className="modal-content">
            <span 
              onClick={() => setModalVisible(false)} 
              className="close"
              aria-label="Cerrar modal"
            >
              &times;
            </span>
            <div dangerouslySetInnerHTML={{ __html: modalContent }} />
          </div>
        </div>
      )}

      {isMobile && (
        <div className="mobile-controls">
          <button className="mobile-button left" aria-label="Mover izquierda">
            猬锔
          </button>
          <button className="mobile-button up" aria-label="Saltar">
            猬锔
          </button>
          <button className="mobile-button right" aria-label="Mover derecha">
            ★
          </button>
        </div>
      )}
    </div>
  );
};

export default Juego;
