// src/components/Jugador.js
import React from 'react';
import { Sprite } from '@pixi/react';
import marioImg from '../assets/mario.png'; // una imagen PNG tipo sprite

const Jugador = ({ x, y }) => {
  return (
    <Sprite
      image={marioImg}
      x={x}
      y={y}
      anchor={0.5}
    />
  );
};

export default Jugador;
