// src/components/Ciudad.js
import React from 'react';
import { Container, Sprite } from '@pixi/react';
import universidadImg from '../assets/universidad.png';
import oficinaImg from '../assets/oficina.png';
import laboratorioImg from '../assets/laboratorio.png';

const Ciudad = () => {
  const handleClick = (seccion) => {
    alert(`Abriendo sección: ${seccion}`);
    // Aquí puedes mostrar un modal o navegar entre vistas
  };

  return (
    <Container>
      <Sprite
        image={universidadImg}
        x={100}
        y={500}
        interactive
        buttonMode
        pointertap={() => handleClick('Educación')}
      />
      <Sprite
        image={oficinaImg}
        x={300}
        y={500}
        interactive
        buttonMode
        pointertap={() => handleClick('Experiencia')}
      />
      <Sprite
        image={laboratorioImg}
        x={500}
        y={500}
        interactive
        buttonMode
        pointertap={() => handleClick('Proyectos')}
      />
    </Container>
  );
};

export default Ciudad;
