import React, { useEffect, useRef, useState } from 'react';
import {
  Application, Assets, Sprite, Container, TilingSprite, Text, TextStyle, Texture
} from 'pixi.js';
import './Juego.css';

const Juego = () => {
  // State management
  const [loading, setLoading] = useState(true);
  const [loadProgress, setLoadProgress] = useState(0);
  const [modalVisible, setModalVisible] = useState(false);
  const [modalContent, setModalContent] = useState('');
  const [muted, setMuted] = useState(false);
  const [isMobile, setIsMobile] = useState(false);

  // Refs
  const containerRef = useRef(null);
  const audioRef = useRef(null);
  const velocityRef = useRef(0);
  const jumpVelocityRef = useRef(0);
  const isJumpingRef = useRef(false);
  const directionRef = useRef('right');
  const appRef = useRef(null);
  const marioRef = useRef(null);
  const worldRef = useRef(null);
  const fondoRef = useRef(null);
  const bloquesRef = useRef([]);

  // Content for the blocks
  const bloqueContenidos = [
    // Experiencia
    `<h2>Experiencia Profesional</h2>
     <h3>GRUPO DCI (Aug 2023 - Actual)</h3>
     <p><strong>Project Manager & Developer</strong></p>
     <ul>
       <li>Gesti√≥n de documentaci√≥n de proyectos</li>
       <li>Colaboraci√≥n con stakeholders</li>
       <li>Desarrollo de sistema de gesti√≥n</li>
       <li>Dise√±o de base de datos Firebase</li>
     </ul>
     <h3>Align Technology</h3>
     <p><strong>CAD Designer</strong></p>
     <ul>
       <li>Dise√±o de modelos 3D CAD</li>
       <li>Creaci√≥n de documentaci√≥n t√©cnica</li>
       <li>Migraci√≥n de plataforma</li>
     </ul>`,

    // Educaci√≥n
    `<h2>Educaci√≥n</h2>
     <h3>Universidad Fidelitas</h3>
     <p><strong>Ingenier√≠a en Sistemas</strong> (Dic 2025)</p>
     <h3>VMEDU</h3>
     <p><strong>Scrum Fundamentals Certified</strong> (Mar 2023)</p>`,

    // Habilidades T√©cnicas
    `<h2>Habilidades T√©cnicas</h2>
     <div class="skills-columns">
       <div>
         <h3>Lenguajes</h3>
         <ul>
           <li>Dart</li>
           <li>Java</li>
           <li>C#</li>
           <li>PHP</li>
           <li>HTML/CSS</li>
         </ul>
       </div>
       <div>
         <h3>Frameworks</h3>
         <ul>
           <li>FlutterFlow</li>
           <li>ASP.Net</li>
           <li>Blazor</li>
           <li>React</li>
         </ul>
       </div>
       <div>
         <h3>Bases de Datos</h3>
         <ul>
           <li>Firebase</li>
           <li>MySQL</li>
           <li>Oracle</li>
         </ul>
       </div>
     </div>`,

    // Idiomas
    `<h2>Idiomas</h2>
     <ul>
       <li><strong>Espa√±ol:</strong> Nativo (C2)</li>
       <li><strong>Ingl√©s:</strong> Avanzado (B2)</li>
     </ul>`,

    // Contacto
    `<h2>Informaci√≥n de Contacto</h2>
     <ul class="contact-info">
       <li>üìß <strong>Email:</strong> emalva99guz@outlook.com</li>
       <li>üì± <strong>Tel√©fono:</strong> +506 7007 2655</li>
       <li>üìç <strong>Ubicaci√≥n:</strong> Cartago, Costa Rica</li>
       <li>üîó <strong>LinkedIn:</strong> <a href="https://www.linkedin.com/in/emmanuel-malavassi" target="_blank">linkedin.com/in/emmanuel-malavassi</a></li>
     </ul>`,

    // Resumen Profesional
    `<h2>Resumen Profesional</h2>
     <p>Estudiante y Desarrollador en GRUPO DCI con conocimientos en lenguajes de programaci√≥n y metodolog√≠as √°giles.</p>
     <p>Experiencia en gesti√≥n de documentaci√≥n de proyectos y desarrollo de sistemas.</p>`,

    // Proyectos
    `<h2>Proyectos Destacados</h2>
     <h3>Sistema de Gesti√≥n de Empleados</h3>
     <ul>
       <li>Desarrollado con Dart, FlutterFlow y Firebase</li>
       <li>Optimizaci√≥n de registros</li>
     </ul>
     <h3>Migraci√≥n de Plataforma</h3>
     <ul>
       <li>Colaboraci√≥n en proyecto de migraci√≥n</li>
       <li>Capacitaci√≥n de empleados</li>
     </ul>`,

    // Herramientas
    `<h2>Herramientas</h2>
     <div class="tools-grid">
       <div><img src="/assets/figma.png" alt="Figma"> Figma</div>
       <div><img src="/assets/docker.png" alt="Docker"> Docker</div>
       <div><img src="/assets/github.png" alt="GitHub"> GitHub</div>
       <div><img src="/assets/vs.png" alt="VS"> Visual Studio</div>
       <div><img src="/assets/adobe.png" alt="Adobe XD"> Adobe XD</div>
       <div><img src="/assets/vm.png" alt="VMs"> Virtual Machines</div>
     </div>`,

    // Soft Skills
    `<h2>Habilidades Blandas</h2>
     <ul class="soft-skills">
       <li>‚úîÔ∏è Resoluci√≥n de problemas</li>
       <li>‚úîÔ∏è Metodolog√≠as √°giles</li>
       <li>‚úîÔ∏è Pensamiento anal√≠tico</li>
       <li>‚úîÔ∏è Gesti√≥n del tiempo</li>
     </ul>`,

    // Extras
    `<h2>Experiencias Adicionales</h2>
     <h3>Biotech Lab</h3>
     <p><strong>Asistente de Laboratorio</strong></p>
     <ul>
       <li>Soporte t√©cnico para sistemas IT</li>
     </ul>
     <h3>Arcos Dorados</h3>
     <p><strong>Asociado de Servicio al Cliente</strong></p>
     <ul>
       <li>Operaci√≥n de sistemas punto de venta</li>
     </ul>`
  ];

  const etiquetasBloques = [
    'Experiencia', 'Educaci√≥n', 'Habilidades', 'Idiomas', 'Contacto',
    'Resumen', 'Proyectos', 'Herramientas', 'Soft Skills', 'Extras',
  ];

  // Handle escape key for modal
  useEffect(() => {
    const handleEscape = (e) => {
      if (e.key === 'Escape' && modalVisible) {
        setModalVisible(false);
      }
    };
    window.addEventListener('keydown', handleEscape);
    return () => window.removeEventListener('keydown', handleEscape);
  }, [modalVisible]);

  // Check for mobile devices
  useEffect(() => {
    const checkMobile = () => {
      setIsMobile(window.innerWidth <= 768);
    };
    checkMobile();
    window.addEventListener('resize', checkMobile);
    return () => window.removeEventListener('resize', checkMobile);
  }, []);

  // Create fallback texture
  const createFallbackTexture = (color = 0xff0000, width = 64, height = 64) => {
    const canvas = document.createElement('canvas');
    canvas.width = width;
    canvas.height = height;
    const ctx = canvas.getContext('2d');
    ctx.fillStyle = `#${color.toString(16).padStart(6, '0')}`;
    ctx.fillRect(0, 0, width, height);
    ctx.strokeStyle = '#000000';
    ctx.lineWidth = 2;
    ctx.strokeRect(0, 0, width, height);
    return Texture.from(canvas);
  };

  // Main game setup
  useEffect(() => {
    // Disable scroll
    document.body.style.overflow = 'hidden';
    document.documentElement.style.overflow = 'hidden';

    // Audio setup
    const setupAudio = () => {
      try {
        audioRef.current = new Audio(process.env.PUBLIC_URL + '/assets/musica.mp3');
        audioRef.current.loop = true;
        audioRef.current.volume = 0.05;
        
        const handleFirstInteraction = () => {
          audioRef.current.play().catch(console.warn);
          ['click', 'keydown', 'touchstart'].forEach(evt => 
            document.removeEventListener(evt, handleFirstInteraction)
          );
        };

        ['click', 'keydown', 'touchstart'].forEach(evt => 
          document.addEventListener(evt, handleFirstInteraction)
        );
      } catch (error) {
        console.error('Error al configurar audio:', error);
      }
    };

    setupAudio();

    const cargarJuego = async () => {
      try {
        // Required assets with fallback colors
        const requiredAssets = {
          'bloque.png': { alias: 'bloque', fallbackColor: 0x8B4513 }, // Brown
          'suelo.png': { alias: 'suelo', fallbackColor: 0x228B22 }, // Green
          'ciudad.png': { alias: 'fondo', fallbackColor: 0x87CEEB }, // Sky blue
          'nube.png': { alias: 'nube', fallbackColor: 0xFFFFFF }, // White
          'farol.png': { alias: 'farol', fallbackColor: 0xFFD700 }, // Gold
          'arbusto.png': { alias: 'arbusto', fallbackColor: 0x006400 }, // Dark green
          'tile000.png': { alias: 'walk0', fallbackColor: 0xFF0000 }, // Red
          'tile001.png': { alias: 'walk1', fallbackColor: 0xFF7F00 }, // Orange
          'tile002.png': { alias: 'walk2', fallbackColor: 0xFFFF00 }, // Yellow
          'tile003.png': { alias: 'walk3', fallbackColor: 0x00FF00 }, // Green
          'tile004.png': { alias: 'walk4', fallbackColor: 0x0000FF }, // Blue
          'tile005.png': { alias: 'walk5', fallbackColor: 0x4B0082 }, // Indigo
          'tile006.png': { alias: 'walk6', fallbackColor: 0x9400D3 }, // Violet
          'tile007.png': { alias: 'walk7', fallbackColor: 0xFF1493 }  // Deep pink
        };

        // Initialize PixiJS
const app = new Application({
  width: window.innerWidth,
  height: window.innerHeight,
  backgroundColor: 0x87ceeb,
  resolution: window.devicePixelRatio || 1,
  resizeTo: window,
  antialias: true
});

        appRef.current = app;
        containerRef.current.appendChild(app.canvas);

        // Setup assets with enhanced error handling
        Assets.init({ 
          basePath: process.env.PUBLIC_URL + '/assets',
          preferWorkers: true
        });

        // Custom loader with fallback
        const loadAsset = async (src, alias, fallbackColor) => {
          try {
            // First try normal loading
            await Assets.load({ src, alias });
            const texture = Assets.get(alias);
            
            if (!texture.valid) {
              throw new Error('Texture not valid');
            }
            
            return texture;
          } catch (error) {
            console.warn(`Fallback created for ${src}:`, error);
            return createFallbackTexture(fallbackColor);
          }
        };

        // Load all assets with progress tracking
        const assetPromises = Object.entries(requiredAssets).map(
          ([src, { alias, fallbackColor }], index) => {
            return loadAsset(src, alias, fallbackColor).then(() => {
              setLoadProgress(Math.round(((index + 1) / Object.keys(requiredAssets).length) * 100));
            });
          }
        );

        await Promise.all(assetPromises);
        setLoading(false);

        // Get textures (now guaranteed to have either real or fallback textures)
        const textures = {
          bloque: Assets.get('bloque'),
          suelo: Assets.get('suelo'),
          fondo: Assets.get('fondo'),
          nube: Assets.get('nube'),
          farol: Assets.get('farol'),
          arbusto: Assets.get('arbusto'),
          walk0: Assets.get('walk0'),
          walk1: Assets.get('walk1'),
          walk2: Assets.get('walk2'),
          walk3: Assets.get('walk3'),
          walk4: Assets.get('walk4'),
          walk5: Assets.get('walk5'),
          walk6: Assets.get('walk6'),
          walk7: Assets.get('walk7')
        };

        const framesLeft = [
          textures.walk0, 
          textures.walk1, 
          textures.walk2, 
          textures.walk3
        ];
        
        const framesRight = [
          textures.walk7, 
          textures.walk6, 
          textures.walk5, 
          textures.walk4
        ];

        // Setup background
        const fondo = new TilingSprite(textures.fondo, 6000, app.screen.height);
        fondo.zIndex = -1;
        fondoRef.current = fondo;
        app.stage.addChild(fondo);

        // Create world container
        const world = new Container();
        worldRef.current = world;
        app.stage.addChild(world);

        // Welcome cloud
        const nubeInicio = new Sprite(textures.nube);
        nubeInicio.position.set(200, 200);
        nubeInicio.scale.set(0.8);
        nubeInicio.alpha = 0.9;
        world.addChild(nubeInicio);

        const bienvenidaText = new Text(
          '¬°Bienvenido!\nUsa flechas para moverte\nEscape para cerrar modales',
          new TextStyle({
            fontFamily: 'Arial',
            fontSize: 16,
            fill: 'black',
            align: 'center',
            wordWrap: true,
            wordWrapWidth: 200,
          })
        );
        bienvenidaText.anchor.set(0.5);
        bienvenidaText.position.set(
          nubeInicio.x + nubeInicio.width / 2,
          nubeInicio.y + nubeInicio.height / 2
        );
        world.addChild(bienvenidaText);

        // Clouds
        for (let i = 0; i < 10; i++) {
          const nube = new Sprite(textures.nube);
          nube.position.set(i * 800, 80 + Math.random() * 20);
          nube.alpha = 0.9;
          nube.scale.set(0.5);
          world.addChild(nube);
        }

        // Ground
        for (let i = 0; i < 100; i++) {
          const suelo = new Sprite(textures.suelo);
          suelo.scale.set(0.4);
          suelo.position.set(i * (suelo.width * 0.3), 750);
          world.addChild(suelo);
        }

        // Interactive blocks
        const bloques = [];
        bloquesRef.current = bloques;
        for (let i = 0; i < 10; i++) {
          const bloque = new Sprite(textures.bloque);
          bloque.scale.set(0.05);
          bloque.position.set(900 + i * 400, 470);
          bloque.eventMode = 'static';
          bloque.cursor = 'pointer';
          bloque.bloqueID = i + 1;
          world.addChild(bloque);
          bloques.push(bloque);

          const textoBurbuja = new Text(etiquetasBloques[i], new TextStyle({
            fontFamily: 'Arial',
            fontSize: 18,
            fill: 'white',
            stroke: '#000',
            strokeThickness: 3,
            align: 'center',
          }));
          textoBurbuja.anchor.set(0.5);
          textoBurbuja.position.set(
            bloque.x + (bloque.width * 0.5),
            bloque.y - 40
          );
          world.addChild(textoBurbuja);
        }

        // Decorative elements
        for (let i = 0; i < 5; i++) {
          const farol = new Sprite(textures.farol);
          farol.scale.set(0.58);
          farol.position.set(600 + i * 800, 433);
          world.addChild(farol);
        }

        for (let i = 0; i < 8; i++) {
          const arbusto = new Sprite(textures.arbusto);
          arbusto.position.set(300 + i * 600, 495);
          world.addChild(arbusto);
        }

        // Character
        const mario = new Sprite(framesRight[0]);
        mario.scale.set(0.58);
        mario.position.set(app.screen.width / 4, 750);
        mario.anchor.set(0.5, 1);
        marioRef.current = mario;
        app.stage.addChild(mario);

        // Game loop variables
        let currentFrame = 0;
        let frameCounter = 0;
        const gravity = 1.2;
        const floorY = 750;
        const jumpForce = -22;
        const moveSpeed = 5;

        // Enhanced movement function
        const moverMundo = (delta) => {
          const velocity = velocityRef.current;
          const jumpVelocity = jumpVelocityRef.current;
          const isJumping = isJumpingRef.current;
          const direction = directionRef.current;
          const mario = marioRef.current;
          const world = worldRef.current;
          const fondo = fondoRef.current;
          const bloques = bloquesRef.current;

          // Animation
          if (velocity !== 0) {
            frameCounter += delta;
            if (frameCounter >= 10) {
              frameCounter = 0;
              currentFrame = (currentFrame + 1) % 4;
              mario.texture = direction === 'right' 
                ? framesRight[currentFrame] 
                : framesLeft[currentFrame];
            }
          }

          // Movement
          const deltaSpeed = velocity * moveSpeed * (delta / 16.67);
          
          const limiteIzquierdo = 0;
          const limiteDerecho = -(bloques[bloques.length - 1].x + 400 - app.screen.width);
          const umbralIzquierdo = app.screen.width * 0.3;
          const umbralDerecho = app.screen.width * 0.7;

          if (
            (velocity < 0 && (mario.x > umbralIzquierdo || world.x >= limiteIzquierdo)) ||
            (velocity > 0 && (mario.x < umbralDerecho || world.x <= limiteDerecho))
          ) {
            mario.x += deltaSpeed;
            mario.x = Math.max(0, Math.min(app.screen.width, mario.x));
          } else {
            world.x -= deltaSpeed;
            world.x = Math.max(limiteDerecho, Math.min(limiteIzquierdo, world.x));
            fondo.tilePosition.x += deltaSpeed * 0.15;
          }

          // Jumping
          if (isJumping) {
            const deltaGravity = gravity * (delta / 16.67);
            mario.y += jumpVelocity * (delta / 16.67);
            jumpVelocityRef.current += deltaGravity;
            
            if (mario.y >= floorY) {
              mario.y = floorY;
              isJumpingRef.current = false;
              jumpVelocityRef.current = 0;
            }

            // Block collision
            bloques.forEach((bloque) => {
              const marioBounds = mario.getBounds();
              const bloqueBounds = bloque.getBounds();
              
              const tocaDebajo =
                marioBounds.y <= bloqueBounds.y + 15 &&
                marioBounds.y + marioBounds.height >= bloqueBounds.y &&
                marioBounds.x + marioBounds.width - 10 > bloqueBounds.x &&
                marioBounds.x + 10 < bloqueBounds.x + bloqueBounds.width &&
                jumpVelocity < 0;

              if (tocaDebajo) {
                jumpVelocityRef.current = 8;
                const originalY = bloque.y;
                
                let bounceHeight = 0;
                const bounceUp = () => {
                  bounceHeight += 2;
                  bloque.y = originalY - bounceHeight;
                  if (bounceHeight >= 15) {
                    app.ticker.remove(bounceUp);
                    const bounceDown = () => {
                      bounceHeight -= 2;
                      bloque.y = originalY - bounceHeight;
                      if (bounceHeight <= 0) {
                        bloque.y = originalY;
                        app.ticker.remove(bounceDown);
                      }
                    };
                    app.ticker.add(bounceDown);
                  }
                };
                app.ticker.add(bounceUp);
                
                setModalContent(bloqueContenidos[bloque.bloqueID - 1] || '<p>Contenido no disponible.</p>');
                setModalVisible(true);
              }
            });
          }
        };

        // Start game loop
        app.ticker.add(moverMundo);

        // Enhanced keyboard controls
        const handleKeyDown = (e) => {
          if (!modalVisible) {
            switch(e.key) {
              case 'ArrowRight':
                velocityRef.current = 1;
                directionRef.current = 'right';
                break;
              case 'ArrowLeft':
                velocityRef.current = -1;
                directionRef.current = 'left';
                break;
              case 'ArrowUp':
              case ' ':
                if (!isJumpingRef.current) {
                  isJumpingRef.current = true;
                  jumpVelocityRef.current = jumpForce;
                  if (window.navigator.vibrate) window.navigator.vibrate(30);
                }
                break;
            }
          }
        };

        const handleKeyUp = (e) => {
          if (!modalVisible && (e.key === 'ArrowRight' || e.key === 'ArrowLeft')) {
            velocityRef.current = 0;
          }
        };

        window.addEventListener('keydown', handleKeyDown);
        window.addEventListener('keyup', handleKeyUp);

        // Enhanced touch controls
        const handleTouchStart = (direction) => {
          return (e) => {
            e.preventDefault();
            if (direction === 'up') {
              if (!isJumpingRef.current) {
                isJumpingRef.current = true;
                jumpVelocityRef.current = jumpForce;
              }
            } else {
              velocityRef.current = direction === 'right' ? 1 : -1;
              directionRef.current = direction;
            }
            if (window.navigator.vibrate) window.navigator.vibrate(20);
          };
        };

        const handleTouchEnd = (e) => {
          e.preventDefault();
          velocityRef.current = 0;
        };

        // Prevent touch scroll
        const preventTouch = (e) => e.preventDefault();
        document.addEventListener('touchmove', preventTouch, { passive: false });

        return () => {
          window.removeEventListener('keydown', handleKeyDown);
          window.removeEventListener('keyup', handleKeyUp);
          document.removeEventListener('touchmove', preventTouch);
          app.destroy(true, true);
          document.body.style.overflow = '';
          document.documentElement.style.overflow = '';
        };
      } catch (err) {
        console.error('‚ùå Error en el juego:', err);
        setModalContent(`
          <h2>Error al cargar el juego</h2>
          <p>${err.message}</p>
          <p>Detalles t√©cnicos:</p>
          <ul>
            <li>Verifica que todas las im√°genes est√©n en public/assets/</li>
            <li>Revisa la consola para m√°s detalles</li>
          </ul>
        `);
        setModalVisible(true);
        setLoading(false);
      }
    };

    cargarJuego();

    return () => {
      if (appRef.current) {
        appRef.current.destroy(true);
      }
      if (audioRef.current) {
        audioRef.current.pause();
        audioRef.current = null;
      }
      document.body.style.overflow = '';
      document.documentElement.style.overflow = '';
    };
  }, []);

  return (
    <div className="game-container">
      {loading && (
        <div className="loading-screen">
          <div className="loading-progress">
            <div className="progress-bar" style={{ width: `${loadProgress}%` }}></div>
            <div className="progress-text">Cargando... {loadProgress}%</div>
          </div>
        </div>
      )}

      <header className="header-juego">
        <h1>Emanuel Alvarado Guzman</h1>
        <p>Desarrollador Full Stack | Analista de datos</p>

        <div className="redes">
          <a href="https://www.linkedin.com/in/emmanuel-malavassi" target="_blank" rel="noopener noreferrer">
            <img src={process.env.PUBLIC_URL + "/assets/linkedin.png"} alt="LinkedIn" />
          </a>
          <a href="mailto:emalva99guz@outlook.com">
            <img src={process.env.PUBLIC_URL + "/assets/email.png"} alt="Email" />
          </a>
          <a href="https://github.com/hdecache99" target="_blank" rel="noopener noreferrer">
            <img src={process.env.PUBLIC_URL + "/assets/github.png"} alt="GitHub" />
          </a>
          <button
            className="mute-button"
            onClick={() => {
              const current = audioRef.current;
              if (current) {
                current.muted = !current.muted;
                setMuted(current.muted);
              }
            }}
            aria-label={muted ? "Unmute" : "Mute"}
          >
            {muted ? 'üîá' : 'üîä'}
          </button>
        </div>
      </header>

      <div ref={containerRef} className="pixi-container" />

      {modalVisible && (
        <div className="modal">
          <div className="modal-content" role="dialog" aria-labelledby="modal-title">
            <span 
              onClick={() => setModalVisible(false)} 
              className="close"
              aria-label="Cerrar modal"
            >
              &times;
            </span>
            <div dangerouslySetInnerHTML={{ __html: modalContent }} />
            <p className="modal-hint">(Presiona Escape para cerrar)</p>
          </div>
        </div>
      )}

      {isMobile && (
        <div className="mobile-controls">
          <button
            className="mobile-button left"
            onTouchStart={(e) => { 
              e.preventDefault();
              velocityRef.current = -1;
              directionRef.current = 'left';
              if (window.navigator.vibrate) window.navigator.vibrate(20);
            }}
            onTouchEnd={(e) => { 
              e.preventDefault();
              velocityRef.current = 0; 
            }}
            aria-label="Mover izquierda"
          >
            ‚¨ÖÔ∏è
          </button>
          <button
            className="mobile-button up"
            onTouchStart={(e) => {
              e.preventDefault();
              if (!isJumpingRef.current) {
                isJumpingRef.current = true;
                jumpVelocityRef.current = -22;
                if (window.navigator.vibrate) window.navigator.vibrate(20);
              }
            }}
            aria-label="Saltar"
          >
            ‚¨ÜÔ∏è
          </button>
          <button
            className="mobile-button right"
            onTouchStart={(e) => { 
              e.preventDefault();
              velocityRef.current = 1;
              directionRef.current = 'right';
              if (window.navigator.vibrate) window.navigator.vibrate(20);
            }}
            onTouchEnd={(e) => { 
              e.preventDefault();
              velocityRef.current = 0; 
            }}
            aria-label="Mover derecha"
          >
            ‚û°Ô∏è
          </button>
        </div>
      )}
    </div>
  );
};

export default Juego;
