import React, { useEffect, useRef, useState } from 'react';
import * as PIXI from 'pixi.js';
import './Juego.css';

const Juego = () => {
  const [loading, setLoading] = useState(true);
  const [loadProgress, setLoadProgress] = useState(0);
  const [modalVisible, setModalVisible] = useState(false);
  const [modalContent, setModalContent] = useState('');
  const [muted, setMuted] = useState(false);
  const [isMobile, setIsMobile] = useState(false);

  const containerRef = useRef(null);
  const audioRef = useRef(null);
  const appRef = useRef(null);

  const bloqueContenidos = [
    '<h2>Experiencia</h2><p>5 a√±os como desarrollador Full Stack...</p>',
    '<h2>Educaci√≥n</h2><p>Ingenier√≠a en Sistemas...</p>',
    '<h2>Habilidades</h2><p>React, Node.js, Python...</p>',
    '<h2>Idiomas</h2><p>Espa√±ol (nativo), Ingl√©s (avanzado)...</p>',
    '<h2>Contacto</h2><p>Email: ejemplo@email.com<br>Tel: +123456789</p>'
  ];
  const etiquetasBloques = ['Experiencia', 'Educaci√≥n', 'Habilidades', 'Idiomas', 'Contacto'];

  // Escape key to close modal
  useEffect(() => {
    const onEsc = (e) => { if (e.key === 'Escape') setModalVisible(false); };
    window.addEventListener('keydown', onEsc);
    return () => window.removeEventListener('keydown', onEsc);
  }, []);

  // Mobile detection
  useEffect(() => {
    const checkMobile = () => setIsMobile(window.innerWidth <= 768);
    checkMobile();
    window.addEventListener('resize', checkMobile);
    return () => window.removeEventListener('resize', checkMobile);
  }, []);

  useEffect(() => {
    document.body.style.overflow = 'hidden';

    // Setup audio
    audioRef.current = new Audio('/assets/musica.mp3');
    audioRef.current.loop = true;
    audioRef.current.volume = 0.05;
    const playAudio = () => {
      audioRef.current.play().catch(() => {});
      ['click', 'keydown', 'touchstart'].forEach(evt => document.removeEventListener(evt, playAudio));
    };
    ['click', 'keydown', 'touchstart'].forEach(evt => document.addEventListener(evt, playAudio));

    // Init PIXI
    const app = new PIXI.Application({
      width: window.innerWidth,
      height: window.innerHeight,
      backgroundColor: 0x87ceeb,
      resolution: window.devicePixelRatio || 1,
      resizeTo: window
    });
    appRef.current = app;
    containerRef.current.appendChild(app.view);

    // Asset list
    const assets = {
      bloque: '/assets/bloque.png',
      suelo: '/assets/suelo.png',
      fondo: '/assets/ciudad.png',
      nube: '/assets/nube.png',
      farol: '/assets/farol.png',
      arbusto: '/assets/arbusto.png',
      walk0: '/assets/tile000.png',
      walk1: '/assets/tile001.png',
      walk2: '/assets/tile002.png',
      walk3: '/assets/tile003.png',
      walk4: '/assets/tile004.png',
      walk5: '/assets/tile005.png',
      walk6: '/assets/tile006.png',
      walk7: '/assets/tile007.png'
    };

    const loader = new PIXI.Loader();
    Object.entries(assets).forEach(([key, url]) => loader.add(key, url));

    loader.onProgress.add((loader) => setLoadProgress(Math.round(loader.progress)));

    loader.load((loader, resources) => {
      setLoading(false);

      const { width, height } = app.screen;
      const world = new PIXI.Container();
      app.stage.addChild(world);

      // Background
      const fondo = new PIXI.TilingSprite(resources.fondo.texture, 6000, height);
      fondo.zIndex = -1;
      app.stage.addChild(fondo);

      // Clouds
      for (let i = 0; i < 5; i++) {
        const nube = new PIXI.Sprite(resources.nube.texture);
        nube.position.set(i * 800, 100 + Math.random() * 50);
        nube.scale.set(0.6);
        nube.alpha = 0.8;
        world.addChild(nube);
      }

      // Ground
      const sueloWidth = resources.suelo.texture.width * 0.4;
      for (let i = 0; i < 50; i++) {
        const suelo = new PIXI.Sprite(resources.suelo.texture);
        suelo.scale.set(0.4);
        suelo.position.set(i * sueloWidth, height - 150);
        world.addChild(suelo);
      }

      // Interactive blocks
      const bloques = [];
      etiquetasBloques.forEach((etiqueta, i) => {
        const bloque = new PIXI.Sprite(resources.bloque.texture);
        bloque.scale.set(0.05);
        bloque.position.set(800 + i * 450, height - 300);
        bloque.interactive = true;
        bloque.cursor = 'pointer';
        bloque.on('pointerdown', () => {
          setModalContent(bloqueContenidos[i]);
          setModalVisible(true);
        });
        world.addChild(bloque);
        bloques.push(bloque);

        const label = new PIXI.Text(etiqueta, {
          fontFamily: 'Arial', fontSize: 18, fill: 0xffffff, stroke: 0x000000, strokeThickness: 3
        });
        label.anchor.set(0.5);
        label.position.set(bloque.x + bloque.width / 2, bloque.y - 40);
        world.addChild(label);
      });

      // Decor
      [700, 1600, 2500].forEach((xPos) => {
        const farol = new PIXI.Sprite(resources.farol.texture);
        farol.scale.set(0.5);
        farol.position.set(xPos, height - 350);
        world.addChild(farol);

        const arbusto = new PIXI.Sprite(resources.arbusto.texture);
        arbusto.position.set(xPos - 300, height - 280);
        world.addChild(arbusto);
      });

      // Player
      const framesRight = [0,1,2,3].map(i => resources[`walk${i}`].texture);
      const framesLeft = [7,6,5,4].map(i => resources[`walk${i}`].texture);
      const mario = new PIXI.Sprite(framesRight[0]);
      mario.scale.set(0.5);
      mario.anchor.set(0.5, 1);
      mario.position.set(width / 3, height - 150);
      world.addChild(mario);

      // Constants
      const GRAVITY = 1.2;
      const JUMP_FORCE = -20;
      const MOVE_SPEED = 8;
      const WORLD_WIDTH = 4000;
      const SCREEN_WIDTH = width;
      const FLOOR_Y = height - 150;

      let velocity = 0;
      let jumpVelocity = 0;
      let isJumping = false;
      let direction = 'right';
      let frameCount = 0;
      let currentFrame = 0;

      // Game loop
      app.ticker.add(delta => {
        // Animation
        if (velocity !== 0) {
          frameCount += delta;
          if (frameCount >= 10) {
            frameCount = 0;
            currentFrame = (currentFrame + 1) % 4;
            mario.texture = direction === 'right' ? framesRight[currentFrame] : framesLeft[currentFrame];
          }
        } else {
          mario.texture = direction === 'right' ? framesRight[0] : framesLeft[0];
        }

        // Horizontal movement
        const deltaSpeed = velocity * MOVE_SPEED * (delta / 16.67);
        const worldLeftLimit = 0;
        const worldRightLimit = -(WORLD_WIDTH - SCREEN_WIDTH);
        const cameraThreshold = SCREEN_WIDTH * 0.3;

        if (velocity !== 0) {
          if (velocity > 0) {
            if (world.x > worldRightLimit && mario.x > cameraThreshold) {
              world.x -= deltaSpeed;
              fondo.tilePosition.x += deltaSpeed * 0.2;
            } else {
              mario.x = Math.min(SCREEN_WIDTH - 50, mario.x + deltaSpeed);
            }
          } else {
            if (world.x < worldLeftLimit && mario.x < SCREEN_WIDTH - cameraThreshold) {
              world.x -= deltaSpeed;
              fondo.tilePosition.x += deltaSpeed * 0.2;
            } else {
              mario.x = Math.max(50, mario.x + deltaSpeed);
            }
          }
        }

        // Jump & gravity
        if (isJumping) {
          mario.y += jumpVelocity * (delta / 16.67);
          jumpVelocity += GRAVITY * (delta / 16.67);

          // Collision with floor
          if (mario.y >= FLOOR_Y) {
            mario.y = FLOOR_Y;
            isJumping = false;
            jumpVelocity = 0;
          }
        }
      });

      // Controls
      const keyDown = e => {
        if (!modalVisible) switch (e.key) {
          case 'ArrowRight': velocity = 1; direction = 'right'; break;
          case 'ArrowLeft': velocity = -1; direction = 'left'; break;
          case 'ArrowUp':
          case ' ': if (!isJumping && mario.y >= FLOOR_Y) { isJumping = true; jumpVelocity = JUMP_FORCE; } break;
        }
      };
      const keyUp = e => {
        if (e.key === 'ArrowRight' && velocity > 0) velocity = 0;
        if (e.key === 'ArrowLeft' && velocity < 0) velocity = 0;
      };
      window.addEventListener('keydown', keyDown);
      window.addEventListener('keyup', keyUp);

      // Mobile controls
      if (isMobile) {
        setTimeout(() => {
          document.querySelector('.mobile-button.left')?.addEventListener('touchstart', () => { velocity = -1; direction = 'left'; });
          document.querySelector('.mobile-button.left')?.addEventListener('touchend', () => { if (velocity === -1) velocity = 0; });
          document.querySelector('.mobile-button.right')?.addEventListener('touchstart', () => { velocity = 1; direction = 'right'; });
          document.querySelector('.mobile-button.right')?.addEventListener('touchend', () => { if (velocity === 1) velocity = 0; });
          document.querySelector('.mobile-button.up')?.addEventListener('touchstart', () => { if (!isJumping) { isJumping = true; jumpVelocity = JUMP_FORCE; } });
        }, 100);
      }

      // Cleanup
      return () => {
        window.removeEventListener('keydown', keyDown);
        window.removeEventListener('keyup', keyUp);
        app.destroy(true, { children: true });
        audioRef.current.pause();
      };
    });

    return () => {
      document.body.style.overflow = '';
      if (appRef.current) appRef.current.destroy(true);
      if (audioRef.current) audioRef.current.pause();
    };
  }, [isMobile]);

  return (
    <div className="game-container">
      {loading && (
        <div className="loading-screen">
          <div className="loading-progress">
            <div className="progress-bar" style={{ width: `${loadProgress}%` }} />
            <div className="progress-text">Cargando... {loadProgress}%</div>
          </div>
        </div>
      )}
      <header className="header-juego">
        <h1>Mi Portfolio Interactivo</h1>
        <p>Desarrollador Full Stack | Especialista en React</p>
        <div className="redes">
          <a href="https://linkedin.com" target="_blank" rel="noopener noreferrer"><img src="/assets/linkedin.png" alt="LinkedIn" /></a>
          <a href="mailto:ejemplo@email.com"><img src="/assets/email.png" alt="Email" /></a>
          <a href="https://github.com" target="_blank" rel="noopener noreferrer"><img src="/assets/github.png" alt="GitHub" /></a>
          <button className="mute-button" onClick={() => { audioRef.current.muted = !audioRef.current.muted; setMuted(audioRef.current.muted); }}>
            {muted ? 'üîá' : 'üîä'}
          </button>
        </div>
      </header>
      <div ref={containerRef} className="pixi-container" />
      {modalVisible && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={() => setModalVisible(false)}>&times;</span>
            <div dangerouslySetInnerHTML={{ __html: modalContent }} />
            <p className="modal-hint">(Presiona Escape para cerrar)</p>
          </div>
        </div>
      )}
      {isMobile && (
        <div className="mobile-controls">
          <button className="mobile-button left">‚¨ÖÔ∏è</button>
          <button className="mobile-button up">‚¨ÜÔ∏è</button>
          <button className="mobile-button right">‚û°Ô∏è</button>
        </div>
      )}
    </div>
  );
};

export default Juego;
