import React, { useEffect, useRef, useState } from 'react';
import { Application, Assets, Sprite, Container } from 'pixi.js';
import './Juego.css';

const Juego = () => {
  // Estados b치sicos
  const [loading, setLoading] = useState(true);
  
  // Referencias
  const containerRef = useRef(null);
  const appRef = useRef(null);
  const marioRef = useRef(null);
  
  // Movimiento
  const velocity = useRef(0);
  const jumpVelocity = useRef(0);
  const isJumping = useRef(false);
  const direction = useRef('right');

  // Efecto principal del juego
  useEffect(() => {
    document.body.style.overflow = 'hidden';

    const loadGame = async () => {
      try {
        // 1. Inicializar PixiJS
        const app = new Application();
        await app.init({
          width: window.innerWidth,
          height: window.innerHeight,
          backgroundColor: 0x87ceeb,
          resolution: window.devicePixelRatio || 1,
          resizeTo: window
        });
        appRef.current = app;
        containerRef.current.appendChild(app.canvas);

        // 2. Cargar assets (sprites)
        await Assets.init({ basePath: process.env.PUBLIC_URL + '/assets' });
        await Assets.load([
          'tile000.png', 'tile001.png', 'tile002.png', 'tile003.png',
          'tile004.png', 'tile005.png', 'tile006.png', 'tile007.png',
          'suelo.png'
        ]);

        setLoading(false);

        // 3. Obtener texturas
        const textures = {
          walk0: Assets.get('tile000.png'),
          walk1: Assets.get('tile001.png'),
          walk2: Assets.get('tile002.png'),
          walk3: Assets.get('tile003.png'),
          walk4: Assets.get('tile004.png'),
          walk5: Assets.get('tile005.png'),
          walk6: Assets.get('tile006.png'),
          walk7: Assets.get('tile007.png'),
          suelo: Assets.get('suelo.png')
        };

        // Configurar animaciones
        const framesLeft = [textures.walk0, textures.walk1, textures.walk2, textures.walk3];
        const framesRight = [textures.walk7, textures.walk6, textures.walk5, textures.walk4];

        // 4. Crear mundo del juego
        const world = new Container();
        app.stage.addChild(world);

        // 5. Crear suelo
        const suelo = new Sprite(textures.suelo);
        suelo.scale.set(0.4);
        suelo.position.set(0, window.innerHeight - 50);
        suelo.width = window.innerWidth * 2;
        world.addChild(suelo);

        // 6. Crear personaje
        const mario = new Sprite(framesRight[0]);
        mario.scale.set(0.58);
        mario.position.set(window.innerWidth / 4, window.innerHeight - 50);
        mario.anchor.set(0.5, 1);
        marioRef.current = mario;
        world.addChild(mario);

        // 7. Variables del juego
        const gravity = 1.5;
        const floorY = window.innerHeight - 50;
        const jumpForce = -25;
        const moveSpeed = 8;
        let currentFrame = 0;
        let frameCounter = 0;

        // 8. Bucle del juego
        app.ticker.add((delta) => {
          // Animaci칩n
          if (velocity.current !== 0) {
            frameCounter += delta;
            if (frameCounter >= 10) {
              frameCounter = 0;
              currentFrame = (currentFrame + 1) % 4;
              mario.texture = direction.current === 'right' 
                ? framesRight[currentFrame] 
                : framesLeft[currentFrame];
            }
          }

          // Movimiento horizontal
          mario.x += velocity.current * moveSpeed;

          // Limitar movimiento a los bordes de la pantalla
          mario.x = Math.max(mario.width/2, Math.min(window.innerWidth - mario.width/2, mario.x));

          // Salto y gravedad
          if (isJumping.current) {
            mario.y += jumpVelocity.current;
            jumpVelocity.current += gravity;
            
            // Detectar cuando toca el suelo
            if (mario.y >= floorY) {
              mario.y = floorY;
              isJumping.current = false;
              jumpVelocity.current = 0;
            }
          }
        });

        // 9. Controles de teclado
        const handleKeyDown = (e) => {
          switch(e.key) {
            case 'ArrowRight':
              velocity.current = 1;
              direction.current = 'right';
              break;
            case 'ArrowLeft':
              velocity.current = -1;
              direction.current = 'left';
              break;
            case 'ArrowUp':
              if (!isJumping.current && mario.y >= floorY - 1) {
                isJumping.current = true;
                jumpVelocity.current = jumpForce;
              }
              break;
          }
        };

        const handleKeyUp = (e) => {
          if (e.key === 'ArrowRight' || e.key === 'ArrowLeft') {
            velocity.current = 0;
          }
        };

        window.addEventListener('keydown', handleKeyDown);
        window.addEventListener('keyup', handleKeyUp);

        // Limpieza
        return () => {
          window.removeEventListener('keydown', handleKeyDown);
          window.removeEventListener('keyup', handleKeyUp);
          app.destroy(true);
          document.body.style.overflow = '';
        };

      } catch (err) {
        console.error('Error loading game:', err);
        setLoading(false);
      }
    };

    loadGame();

    return () => {
      if (appRef.current) {
        appRef.current.destroy(true);
      }
    };
  }, []);

  return (
    <div className="game-container">
      {loading && (
        <div className="loading-screen">
          <div className="progress-text">Cargando...</div>
        </div>
      )}
      <div ref={containerRef} className="pixi-container" />
    </div>
  );
};

export default Juego;
