import React, { useEffect, useRef } from 'react';
import * as PIXI from 'pixi.js';
import './Juego.css';
import Jugador from './Jugador';
import Ciudad from './Ciudad';

const Juego = () => {
  const gameRef = useRef(null);
  const appRef = useRef(null);
  const jugadorRef = useRef(null);
  const ciudadRef = useRef(null);
  
  useEffect(() => {
    // Inicializar la aplicación PixiJS
    const app = new PIXI.Application({
      width: window.innerWidth,
      height: window.innerHeight,
      backgroundColor: 0x1099bb,
      resolution: window.devicePixelRatio || 1,
      autoDensity: true
    });
    
    gameRef.current.appendChild(app.view);
    appRef.current = app;
    
    // Crear la ciudad (escenario)
    const ciudad = new Ciudad(app);
    ciudadRef.current = ciudad;
    
    // Crear al jugador
    const jugador = new Jugador(app, ciudad);
    jugadorRef.current = jugador;
    
    // Configurar el scroll horizontal
    app.stage.position.x = 0;
    
    // Manejar redimensionamiento
    const handleResize = () => {
      app.renderer.resize(window.innerWidth, window.innerHeight);
    };
    
    window.addEventListener('resize', handleResize);
    
    return () => {
      window.removeEventListener('resize', handleResize);
      app.destroy(true);
    };
  }, []);
  
  return <div ref={gameRef} className="game-container"></div>;
};

export default Juego;
