import React, { useEffect, useRef, useState } from 'react';
import {
  Application,
  Assets,
  Sprite,
  Container,
  TilingSprite,
  Text,
  TextStyle,
  Texture
} from 'pixi.js';
import './Juego.css';

const bloqueContenidos = [
  `<h2>Experiencia Profesional</h2>‚Ä¶`,    // 1
  `<h2>Educaci√≥n</h2>‚Ä¶`,                  // 2
  `<h2>Habilidades T√©cnicas</h2>‚Ä¶`,       // 3
  `<h2>Idiomas</h2>‚Ä¶`,                    // 4
  `<h2>Contacto</h2>‚Ä¶`,                   // 5
  `<h2>Resumen Profesional</h2>‚Ä¶`,        // 6
  `<h2>Proyectos Destacados</h2>‚Ä¶`,       // 7
  `<h2>Herramientas</h2>‚Ä¶`,               // 8
  `<h2>Habilidades Blandas</h2>‚Ä¶`,        // 9
  `<h2>Experiencias Adicionales</h2>‚Ä¶`,   // 10
];

const etiquetasBloques = [
  'Experiencia',
  'Educaci√≥n',
  'Habilidades',
  'Idiomas',
  'Contacto',
  'Resumen',
  'Proyectos',
  'Herramientas',
  'Soft Skills',
  'Extras',
];

const SPRITE_PATHS = [
  { file: 'tile000.png', alias: 'walk0' },
  { file: 'tile001.png', alias: 'walk1' },
  { file: 'tile002.png', alias: 'walk2' },
  { file: 'tile003.png', alias: 'walk3' },
  { file: 'tile004.png', alias: 'walk4' },
  { file: 'tile005.png', alias: 'walk5' },
  { file: 'tile006.png', alias: 'walk6' },
  { file: 'tile007.png', alias: 'walk7' },
];

export default function Juego() {
  const [loading, setLoading] = useState(true);
  const [progress, setProgress] = useState(0);
  const [modal, setModal] = useState({ visible: false, content: '' });
  const [muted, setMuted] = useState(false);
  const [isMobile, setIsMobile] = useState(false);

  const containerRef = useRef(null);
  const audioRef = useRef(null);
  const appRef = useRef(null);
  const refs = {
    velocity: useRef(0),
    jumpVel: useRef(0),
    jumping: useRef(false),
    dir: useRef('right'),
    mario: useRef(null),
    world: useRef(null),
    fondo: useRef(null),
    bloques: useRef([]),
    suelo: useRef(null),
  };

  const createFallback = (color = 0xff0000, w = 64, h = 64) => {
    const cvs = document.createElement('canvas');
    cvs.width = w;
    cvs.height = h;
    const ctx = cvs.getContext('2d');
    ctx.fillStyle = `#${color.toString(16).padStart(6, '0')}`;
    ctx.fillRect(0, 0, w, h);
    return Texture.from(cvs);
  };

  useEffect(() => {
    const onResize = () => setIsMobile(window.innerWidth <= 768);
    const onEsc = e =>
      e.key === 'Escape' && modal.visible && setModal({ visible: false, content: '' });

    window.addEventListener('resize', onResize);
    window.addEventListener('keydown', onEsc);
    onResize();
    return () => {
      window.removeEventListener('resize', onResize);
      window.removeEventListener('keydown', onEsc);
    };
  }, [modal.visible]);

  useEffect(() => {
    document.body.style.overflow = 'hidden';

    // Audio de fondo
    audioRef.current = new Audio(process.env.PUBLIC_URL + '/assets/musica.mp3');
    audioRef.current.loop = true;
    audioRef.current.volume = 0.05;
    const playOnInteract = () => {
      audioRef.current.play().catch(() => {});
      ['click', 'keydown', 'touchstart'].forEach(evt =>
        document.removeEventListener(evt, playOnInteract)
      );
    };
    ['click', 'keydown', 'touchstart'].forEach(evt =>
      document.addEventListener(evt, playOnInteract)
    );

    // Inicializar PixiJS
    const initPixiApp = async () => {
      try {
        // 1) Crear app
        const app = new Application({
          width: window.innerWidth,
          height: window.innerHeight,
          backgroundColor: 0x87ceeb,
          resolution: window.devicePixelRatio || 1,
          antialias: true,
        });
        
        // Verificar que el contenedor existe antes de a√±adir la vista
        if (containerRef.current) {
          containerRef.current.appendChild(app.view);
          appRef.current = app;
        } else {
          throw new Error("Container ref is not available");
        }

        // Resto del c√≥digo de inicializaci√≥n...
        const handleAppResize = () => {
          app.renderer.resize(window.innerWidth, window.innerHeight);
          if (refs.suelo.current) {
            refs.suelo.current.width = window.innerWidth * 2;
            refs.suelo.current.y = window.innerHeight - 50;
          }
        };
        window.addEventListener('resize', handleAppResize);

        // Carga de assets
        await Assets.init({ basePath: process.env.PUBLIC_URL + '/assets' });
        const list = [
          ...SPRITE_PATHS,
          { file: 'suelo.png', alias: 'suelo' },
          { file: 'fondo.png', alias: 'fondo' },
          { file: 'bloque.png', alias: 'bloque' },
          { file: 'nube.png', alias: 'nube' },
          { file: 'farol.png', alias: 'farol' },
          { file: 'arbusto.png', alias: 'arbusto' },
        ];
        
        for (let i = 0; i < list.length; i++) {
          const { file, alias } = list[i];
          try {
            await Assets.load({ src: file, alias });
          } catch {
            Assets.cache.add(alias, createFallback());
          }
          setProgress(Math.round(((i + 1) / list.length) * 100));
        }
        setLoading(false);

        // Resto del c√≥digo del juego...
        const T = key => Assets.get(key);
        const framesL = ['walk0', 'walk1', 'walk2', 'walk3'].map(T);
        const framesR = ['walk7', 'walk6', 'walk5', 'walk4'].map(T);

        const suelo = new TilingSprite(T('suelo'), window.innerWidth * 2, 100);
        suelo.y = window.innerHeight - 50;
        app.stage.addChild(suelo);
        refs.suelo.current = suelo;

        const fondo = new TilingSprite(T('fondo'), 6000, window.innerHeight);
        fondo.zIndex = -1;
        app.stage.addChild(fondo);
        refs.fondo.current = fondo;

        const world = new Container();
        app.stage.addChild(world);
        refs.world.current = world;

        for (let i = 0; i < 10; i++) {
          const nube = new Sprite(T('nube'));
          nube.position.set(i * 800, 80 + Math.random() * 20);
          nube.scale.set(0.5);
          world.addChild(nube);
        }

        etiquetasBloques.forEach((etq, i) => {
          const b = new Sprite(T('bloque'));
          b.scale.set(0.05);
          b.position.set(900 + i * 400, window.innerHeight - 280);
          b.interactive = true;
          b.on('pointerdown', () => {
            setModal({ visible: true, content: bloqueContenidos[i] });
          });
          world.addChild(b);
          refs.bloques.current.push(b);

          const label = new Text(etq, new TextStyle({
            fontFamily: 'Arial',
            fontSize: 18,
            fill: 'white',
            stroke: '#000',
            strokeThickness: 3,
          }));
          label.anchor.set(0.5);
          label.position.set(b.x + b.width / 2, b.y - 40);
          world.addChild(label);
        });

        const mario = new Sprite(framesR[0]);
        mario.anchor.set(0.5, 1);
        mario.position.set(app.screen.width / 4, window.innerHeight - 50);
        mario.scale.set(0.58);
        world.addChild(mario);
        refs.mario.current = mario;

        let frame = 0, counter = 0;
        const gravity = 1.5, jumpF = -25, speed = 10;

        app.ticker.add(delta => {
          const vel = refs.velocity.current;
          const dir = refs.dir.current;

          if (vel !== 0) {
            counter += delta;
            if (counter >= 10) {
              counter = 0;
              frame = (frame + 1) % 4;
              mario.texture = dir === 'right' ? framesR[frame] : framesL[frame];
            }
          } else {
            mario.texture = dir === 'right' ? framesR[0] : framesL[0];
          }

          if (refs.jumping.current) {
            mario.y += refs.jumpVel.current;
            refs.jumpVel.current += gravity;
            if (mario.y >= window.innerHeight - 50) {
              mario.y = window.innerHeight - 50;
              refs.jumping.current = false;
              refs.jumpVel.current = 0;
            }
          }

          const dx = vel * speed * (delta / 16.67);
          const worldLeft = 0;
          const worldRight = -(world.width - app.screen.width);
          const leftT = app.screen.width * 0.3;
          const rightT = app.screen.width * 0.7;

          if (vel < 0) {
            if (mario.x > leftT || world.x >= worldLeft) mario.x += dx;
            else {
              world.x -= dx;
              fondo.tilePosition.x += dx * 0.2;
            }
          } else if (vel > 0) {
            if (mario.x < rightT || world.x <= worldRight) mario.x += dx;
            else {
              world.x -= dx;
              fondo.tilePosition.x += dx * 0.2;
            }
          }

          mario.x = Math.max(0, Math.min(app.screen.width, mario.x));
          world.x = Math.max(worldRight, Math.min(worldLeft, world.x));
        });

        const onKeyDown = e => {
          if (modal.visible) return;
          if (e.key === 'ArrowRight') { refs.velocity.current = 1; refs.dir.current = 'right'; }
          if (e.key === 'ArrowLeft')  { refs.velocity.current = -1; refs.dir.current = 'left'; }
          if ((e.key === 'ArrowUp' || e.key === ' ') && !refs.jumping.current) {
            refs.jumping.current = true;
            refs.jumpVel.current = jumpF;
            window.navigator.vibrate?.(30);
          }
        };
        const onKeyUp = e => {
          if (['ArrowRight','ArrowLeft'].includes(e.key)) refs.velocity.current = 0;
        };
        window.addEventListener('keydown', onKeyDown);
        window.addEventListener('keyup', onKeyUp);

        const setupTouch = () => {
          ['left','up','right'].forEach(dir=>{
            const btn = document.querySelector(`.mobile-button.${dir}`);
            if (!btn) return;
            btn.addEventListener('touchstart', ev=>{
              ev.preventDefault();
              if (dir==='left')  { refs.velocity.current=-1; refs.dir.current='left'; }
              if (dir==='right') { refs.velocity.current=1;  refs.dir.current='right'; }
              if (dir==='up' && !refs.jumping.current) {
                refs.jumping.current = true; refs.jumpVel.current = jumpF;
              }
              window.navigator.vibrate?.(20);
            });
            btn.addEventListener('touchend', ev=>{
              ev.preventDefault();
              if (dir!=='up') refs.velocity.current = 0;
            });
          });
        };
        if (isMobile) setTimeout(setupTouch, 100);

        return () => {
          window.removeEventListener('resize', handleAppResize);
          window.removeEventListener('keydown', onKeyDown);
          window.removeEventListener('keyup', onKeyUp);
          app.destroy(true, { children: true });
          document.body.style.overflow = '';
        };
      } catch (error) {
        console.error("Error initializing PixiJS:", error);
        setLoading(false);
      }
    };

    initPixiApp();

    return () => {
      if (appRef.current) {
        appRef.current.destroy(true, { children: true });
        appRef.current = null;
      }
      document.body.style.overflow = '';
    };
  }, [isMobile, modal.visible]);

  return (
    <div className="game-container">
      {loading && (
        <div className="loading-screen">
          <div className="progress-bar" style={{ width: `${progress}%` }} />
          <div className="progress-text">Cargando‚Ä¶ {progress}%</div>
        </div>
      )}

      <header className="header-juego">
        <h1>Emanuel Alvarado Guzman</h1>
        <p>Full Stack & Data Analyst</p>
        <div className="redes">
          <button onClick={() => {
            const a = audioRef.current;
            if (a) { a.muted = !a.muted; setMuted(a.muted); }
          }}>
            {muted ? 'üîá' : 'üîä'}
          </button>
        </div>
      </header>

      <div ref={containerRef} className="pixi-container" />

      {modal.visible && (
        <div className="modal">
          <div className="modal-content" role="dialog">
            <span className="close" onClick={() => setModal({ visible: false, content: '' })}>
              &times;
            </span>
            <div dangerouslySetInnerHTML={{ __html: modal.content }} />
            <p className="modal-hint">(Esc para cerrar)</p>
          </div>
        </div>
      )}

      {isMobile && (
        <div className="mobile-controls">
          <button className="mobile-button left" aria-label="Izquierda">‚¨ÖÔ∏è</button>
          <button className="mobile-button up" aria-label="Saltar">‚¨ÜÔ∏è</button>
          <button className="mobile-button right" aria-label="Derecha">‚û°Ô∏è</button>
        </div>
      )}
    </div>
  );
}
