import React from 'react';
import { Application, Assets, Sprite, Container, TilingSprite, Text, TextStyle } from 'pixi.js';
import './Juego.css';

class Juego extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      loading: true,
      loadProgress: 0,
      modalVisible: false,
      modalContent: '',
      muted: false,
      isMobile: window.innerWidth <= 768
    };

    this.containerRef = React.createRef();
    this.audio = null;
    this.app = null;
    this.gameState = {
      velocity: 0,
      jumpVelocity: 0,
      isJumping: false,
      direction: 'right',
      mario: null,
      world: null,
      fondo: null,
      bloques: []
    };

    this.bloqueContenidos = [
      '<h2>Experiencia</h2><p>5 a침os como desarrollador Full Stack...</p>',
      '<h2>Educaci칩n</h2><p>Ingenier칤a en Sistemas...</p>',
      '<h2>Habilidades</h2><p>React, Node.js, Python...</p>',
      '<h2>Idiomas</h2><p>Espa침ol (nativo), Ingl칠s (avanzado)...</p>',
      '<h2>Contacto</h2><p>Email: ejemplo@email.com<br>Tel: +123456789</p>'
    ];
    this.etiquetasBloques = ['Experiencia', 'Educaci칩n', 'Habilidades', 'Idiomas', 'Contacto'];

    // Bind handlers
    this.handleKeyDown = this.handleKeyDown.bind(this);
    this.handleKeyUp = this.handleKeyUp.bind(this);
    this.updateResize = this.updateResize.bind(this);
    this.closeOnEscape = this.closeOnEscape.bind(this);
  }

  componentDidMount() {
    // Escape key to close modal
    window.addEventListener('keydown', this.closeOnEscape);
    // Resize listener
    window.addEventListener('resize', this.updateResize);

    // Prevent scroll
    document.body.style.overflow = 'hidden';

    this.setupAudio();
    this.initGame();
  }

  componentWillUnmount() {
    window.removeEventListener('keydown', this.closeOnEscape);
    window.removeEventListener('resize', this.updateResize);
    window.removeEventListener('keydown', this.handleKeyDown);
    window.removeEventListener('keyup', this.handleKeyUp);
    if (this.app) this.app.destroy(true);
    if (this.audio) this.audio.pause();
    document.body.style.overflow = '';
  }

  updateResize() {
    this.setState({ isMobile: window.innerWidth <= 768 });
  }

  closeOnEscape(e) {
    if (e.key === 'Escape') this.setState({ modalVisible: false });
  }

  setupAudio() {
    this.audio = new Audio('/assets/musica.mp3');
    this.audio.loop = true;
    this.audio.volume = 0.05;
    const playOnce = () => {
      this.audio.play().catch(console.warn);
      ['click', 'keydown', 'touchstart'].forEach(evt => document.removeEventListener(evt, playOnce));
    };
    ['click', 'keydown', 'touchstart'].forEach(evt => document.addEventListener(evt, playOnce));
  }

  async initGame() {
    try {
      const app = new Application({
        width: window.innerWidth,
        height: window.innerHeight,
        backgroundColor: 0x87ceeb,
        resolution: window.devicePixelRatio || 1,
        resizeTo: window
      });
      this.app = app;
      this.containerRef.current.appendChild(app.view);

      // Load assets
      const assets = [
        { name: 'bloque', url: 'bloque.png' },
        { name: 'suelo', url: 'suelo.png' },
        { name: 'fondo', url: 'ciudad.png' },
        { name: 'nube', url: 'nube.png' },
        { name: 'farol', url: 'farol.png' },
        { name: 'arbusto', url: 'arbusto.png' },
        { name: 'walk0', url: 'tile000.png' },
        // ... hasta walk7
      ];

      for (let i = 0; i < assets.length; i++) {
        await Assets.load(assets[i].url);
        this.setState({ loadProgress: Math.round(((i + 1) / assets.length) * 100) });
      }
      this.setState({ loading: false });

      // Prepare textures
      const textures = {};
      assets.forEach(a => { textures[a.name] = Assets.get(a.url); });
      const framesLeft = [textures.walk0, textures.walk1, textures.walk2, textures.walk3];
      const framesRight = [textures.walk7, textures.walk6, textures.walk5, textures.walk4];

      // Fondo
      const fondo = new TilingSprite(textures.fondo, 6000, app.screen.height);
      fondo.zIndex = -1;
      this.gameState.fondo = fondo;
      app.stage.addChild(fondo);

      // Mundo
      const world = new Container();
      this.gameState.world = world;
      app.stage.addChild(world);

      // A침adir sprites: nubes, suelo, bloques, decoraciones, mario...
      // (Similar a tu c칩digo actual, adaptado a la clase)

      // Configurar loop y controles
      window.addEventListener('keydown', this.handleKeyDown);
      window.addEventListener('keyup', this.handleKeyUp);

    } catch (error) {
      console.error('Error al cargar el juego:', error);
      this.setState({ loading: false, modalContent: `<h2>Error al cargar</h2><p>${error.message}</p>` , modalVisible: true});
    }
  }

  handleKeyDown(e) {
    if (this.state.modalVisible) return;
    switch (e.key) {
      case 'ArrowRight':
        this.gameState.velocity = 1;
        this.gameState.direction = 'right';
        break;
      case 'ArrowLeft':
        this.gameState.velocity = -1;
        this.gameState.direction = 'left';
        break;
      case 'ArrowUp':
      case ' ':
        if (!this.gameState.isJumping) {
          this.gameState.isJumping = true;
          this.gameState.jumpVelocity = -20;
        }
        break;
      default:
        break;
    }
  }

  handleKeyUp(e) {
    if (e.key === 'ArrowRight' && this.gameState.velocity > 0) {
      this.gameState.velocity = 0;
    } else if (e.key === 'ArrowLeft' && this.gameState.velocity < 0) {
      this.gameState.velocity = 0;
    }
  }

  render() {
    return (
      <div className="game-container">
        {this.state.loading && (
          <div className="loading-screen">
            <div className="loading-progress">
              <div className="progress-bar" style={{ width: `${this.state.loadProgress}%` }} />
              <div className="progress-text">Cargando... {this.state.loadProgress}%</div>
            </div>
          </div>
        )}
        <header className="header-juego">
          <h1>Mi Portfolio Interactivo</h1>
          <p>Desarrollador Full Stack | Especialista en React</p>
          <div className="redes">
            {/* Enlaces y bot칩n de mute */}
            <button onClick={() => {
              if (this.audio) {
                this.audio.muted = !this.audio.muted;
                this.setState({ muted: this.audio.muted });
              }
            }}>
              {this.state.muted ? '游댆' : '游댉'}
            </button>
          </div>
        </header>
        <div ref={this.containerRef} className="pixi-container" />
        {this.state.modalVisible && (
          <div className="modal">
            <div className="modal-content">
              <span onClick={() => this.setState({ modalVisible: false })} className="close">&times;</span>
              <div dangerouslySetInnerHTML={{ __html: this.state.modalContent }} />
              <p className="modal-hint">(Presiona Escape para cerrar)</p>
            </div>
          </div>
        )}
        {this.state.isMobile && (
          <div className="mobile-controls">
            <button className="mobile-button left">拘勇</button>
            <button className="mobile-button up">拘勇</button>
            <button className="mobile-button right">俱뫮잺</button>
          </div>
        )}
      </div>
    );
  }
}

export default Juego;
