import React, { useEffect, useRef, useState } from 'react';
import { Application, Assets, Sprite, Container, TilingSprite, Text, TextStyle } from 'pixi.js';
import './Juego.css';

const Juego = () => {
  // Estados
  const [loading, setLoading] = useState(true);
  const [loadProgress, setLoadProgress] = useState(0);
  const [modalVisible, setModalVisible] = useState(false);
  const [modalContent, setModalContent] = useState('');
  const [muted, setMuted] = useState(false);
  const [isMobile, setIsMobile] = useState(false);

  // Referencias
  const containerRef = useRef(null);
  const audioRef = useRef(null);
  const appRef = useRef(null);
  
  // Referencias del juego
  const gameState = useRef({
    velocity: 0,
    jumpVelocity: 0,
    isJumping: false,
    direction: 'right',
    mario: null,
    world: null,
    fondo: null,
    bloques: []
  });

  // Contenido de los bloques
  const bloqueContenidos = [
    '<h2>Experiencia</h2><p>5 a帽os como desarrollador Full Stack...</p>',
    '<h2>Educaci贸n</h2><p>Ingenier铆a en Sistemas...</p>',
    '<h2>Habilidades</h2><p>React, Node.js, Python...</p>',
    '<h2>Idiomas</h2><p>Espa帽ol (nativo), Ingl茅s (avanzado)...</p>',
    '<h2>Contacto</h2><p>Email: ejemplo@email.com<br>Tel: +123456789</p>'
  ];

  const etiquetasBloques = ['Experiencia', 'Educaci贸n', 'Habilidades', 'Idiomas', 'Contacto'];

  // Efectos
  useEffect(() => {
    const handleEscape = (e) => e.key === 'Escape' && setModalVisible(false);
    window.addEventListener('keydown', handleEscape);
    return () => window.removeEventListener('keydown', handleEscape);
  }, []);

  useEffect(() => {
    const checkMobile = () => setIsMobile(window.innerWidth <= 768);
    checkMobile();
    window.addEventListener('resize', checkMobile);
    return () => window.removeEventListener('resize', checkMobile);
  }, []);

  // Inicializaci贸n del juego
  useEffect(() => {
    document.body.style.overflow = 'hidden';

    // Configuraci贸n de audio
    const setupAudio = () => {
      audioRef.current = new Audio('/assets/musica.mp3');
      audioRef.current.loop = true;
      audioRef.current.volume = 0.05;
      
      const handleFirstInteraction = () => {
        audioRef.current.play().catch(console.warn);
        ['click', 'keydown', 'touchstart'].forEach(evt => 
          document.removeEventListener(evt, handleFirstInteraction)
        );
      };

      ['click', 'keydown', 'touchstart'].forEach(evt => 
        document.addEventListener(evt, handleFirstInteraction)
      );
    };

    setupAudio();

    const initGame = async () => {
      try {
        // Inicializar PixiJS
        const app = new Application();
        await app.init({
          width: window.innerWidth,
          height: window.innerHeight,
          backgroundColor: 0x87ceeb,
          resolution: window.devicePixelRatio || 1,
          resizeTo: window
        });
        
        appRef.current = app;
        containerRef.current.appendChild(app.canvas);

        // Cargar assets
        const assets = [
          { alias: 'bloque', src: 'bloque.png' },
          { alias: 'suelo', src: 'suelo.png' },
          { alias: 'fondo', src: 'ciudad.png' },
          { alias: 'nube', src: 'nube.png' },
          { alias: 'farol', src: 'farol.png' },
          { alias: 'arbusto', src: 'arbusto.png' },
          { alias: 'walk0', src: 'tile000.png' },
          { alias: 'walk1', src: 'tile001.png' },
          { alias: 'walk2', src: 'tile002.png' },
          { alias: 'walk3', src: 'tile003.png' },
          { alias: 'walk4', src: 'tile004.png' },
          { alias: 'walk5', src: 'tile005.png' },
          { alias: 'walk6', src: 'tile006.png' },
          { alias: 'walk7', src: 'tile007.png' }
        ];

      await Promise.all(
        assets.map((asset, index) => 
          Assets.load(asset)
            .then(() => {
              setLoadProgress(Math.round(((index + 1) / assets.length) * 100));
            })
            .catch(error => {
              console.error(`Error loading ${asset.src}:`, error);
            })
        )
      );

        setLoading(false);

        // Configurar escena del juego
        const textures = {
          bloque: Assets.get('bloque'),
          suelo: Assets.get('suelo'),
          fondo: Assets.get('fondo'),
          nube: Assets.get('nube'),
          farol: Assets.get('farol'),
          arbusto: Assets.get('arbusto'),
          walk0: Assets.get('walk0'),
          walk1: Assets.get('walk1'),
          walk2: Assets.get('walk2'),
          walk3: Assets.get('walk3'),
          walk4: Assets.get('walk4'),
          walk5: Assets.get('walk5'),
          walk6: Assets.get('walk6'),
          walk7: Assets.get('walk7')
        };

        const framesLeft = [textures.walk0, textures.walk1, textures.walk2, textures.walk3];
        const framesRight = [textures.walk7, textures.walk6, textures.walk5, textures.walk4];

        // Fondo
        const fondo = new TilingSprite(textures.fondo, 6000, app.screen.height);
        fondo.zIndex = -1;
        gameState.current.fondo = fondo;
        app.stage.addChild(fondo);

        // Mundo (contenedor para todos los elementos)
        const world = new Container();
        gameState.current.world = world;
        app.stage.addChild(world);

        // Nubes decorativas
        for (let i = 0; i < 5; i++) {
          const nube = new Sprite(textures.nube);
          nube.position.set(i * 800, 100 + Math.random() * 50);
          nube.scale.set(0.6);
          nube.alpha = 0.8;
          world.addChild(nube);
        }

        // Suelo
        for (let i = 0; i < 50; i++) {
          const suelo = new Sprite(textures.suelo);
          suelo.scale.set(0.4);
          suelo.position.set(i * (suelo.width * 0.4), 750);
          world.addChild(suelo);
        }

        // Bloques interactivos
        const bloques = [];
        for (let i = 0; i < 5; i++) {
          const bloque = new Sprite(textures.bloque);
          bloque.scale.set(0.05);
          bloque.position.set(800 + i * 450, 500);
          bloque.eventMode = 'static';
          bloque.cursor = 'pointer';
          bloque.bloqueID = i;
          world.addChild(bloque);
          bloques.push(bloque);

          const label = new Text(etiquetasBloques[i], new TextStyle({
            fontFamily: 'Arial',
            fontSize: 18,
            fill: 'white',
            stroke: '#000',
            strokeThickness: 3,
            align: 'center'
          }));
          label.anchor.set(0.5);
          label.position.set(bloque.x + bloque.width/2, bloque.y - 40);
          world.addChild(label);
        }
        gameState.current.bloques = bloques;

        // Decoraci贸n (faroles y arbustos)
        for (let i = 0; i < 3; i++) {
          const farol = new Sprite(textures.farol);
          farol.scale.set(0.5);
          farol.position.set(700 + i * 900, 450);
          world.addChild(farol);

          const arbusto = new Sprite(textures.arbusto);
          arbusto.position.set(400 + i * 700, 520);
          world.addChild(arbusto);
        }

        // Personaje principal
        const mario = new Sprite(framesRight[0]);
        mario.scale.set(0.5);
        mario.anchor.set(0.5, 1);
        mario.position.set(app.screen.width / 3, 750);
        gameState.current.mario = mario;
        world.addChild(mario);

        // Constantes del juego
        const GRAVITY = 1.2;
        const JUMP_FORCE = -20;
        const MOVE_SPEED = 8;
        const WORLD_WIDTH = 4000;
        const SCREEN_WIDTH = app.screen.width;
        const FLOOR_Y = 750;

        // Bucle del juego
        let frameCount = 0;
        let currentFrame = 0;
        
        app.ticker.add((delta) => {
          const { velocity, jumpVelocity, isJumping, direction, mario, world, fondo } = gameState.current;

          // Animaci贸n
          if (velocity !== 0) {
            frameCount += delta;
            if (frameCount >= 10) {
              frameCount = 0;
              currentFrame = (currentFrame + 1) % 4;
              mario.texture = direction === 'right' ? framesRight[currentFrame] : framesLeft[currentFrame];
            }
          } else {
            mario.texture = direction === 'right' ? framesRight[0] : framesLeft[0];
          }

          // Movimiento horizontal
          const deltaSpeed = velocity * MOVE_SPEED * (delta / 16.67);
          const worldLeftLimit = 0;
          const worldRightLimit = -(WORLD_WIDTH - SCREEN_WIDTH);
          const cameraThreshold = SCREEN_WIDTH * 0.3;

          if (velocity !== 0) {
            if (velocity > 0) { // Derecha
              if (world.x > worldRightLimit && mario.x > cameraThreshold) {
                world.x -= deltaSpeed;
                fondo.tilePosition.x += deltaSpeed * 0.2;
              } else {
                mario.x = Math.min(SCREEN_WIDTH - 50, mario.x + deltaSpeed);
              }
            } else { // Izquierda
              if (world.x < worldLeftLimit && mario.x < SCREEN_WIDTH - cameraThreshold) {
                world.x -= deltaSpeed;
                fondo.tilePosition.x += deltaSpeed * 0.2;
              } else {
                mario.x = Math.max(50, mario.x + deltaSpeed);
              }
            }
          }

          // Gravedad y salto
          if (isJumping) {
            mario.y += jumpVelocity * (delta / 16.67);
            gameState.current.jumpVelocity += GRAVITY * (delta / 16.67);
            
            if (mario.y >= FLOOR_Y) {
              mario.y = FLOOR_Y;
              gameState.current.isJumping = false;
              gameState.current.jumpVelocity = 0;
            }

            // Colisi贸n con bloques
            bloques.forEach((bloque) => {
              const marioBounds = mario.getBounds();
              const bloqueBounds = bloque.getBounds();
              
              if (marioBounds.y <= bloqueBounds.y + 15 &&
                  marioBounds.y + marioBounds.height >= bloqueBounds.y &&
                  marioBounds.x + marioBounds.width - 15 > bloqueBounds.x &&
                  marioBounds.x + 15 < bloqueBounds.x + bloqueBounds.width &&
                  gameState.current.jumpVelocity < 0) {
                
                gameState.current.jumpVelocity = 8;
                setModalContent(bloqueContenidos[bloque.bloqueID]);
                setModalVisible(true);
              }
            });
          }
        });

        // Controles de teclado
        const handleKeyDown = (e) => {
          if (!modalVisible) {
            switch(e.key) {
              case 'ArrowRight':
                gameState.current.velocity = 1;
                gameState.current.direction = 'right';
                break;
              case 'ArrowLeft':
                gameState.current.velocity = -1;
                gameState.current.direction = 'left';
                break;
              case 'ArrowUp':
              case ' ':
                if (!gameState.current.isJumping && gameState.current.mario.y >= FLOOR_Y - 1) {
                  gameState.current.isJumping = true;
                  gameState.current.jumpVelocity = JUMP_FORCE;
                }
                break;
            }
          }
        };

        const handleKeyUp = (e) => {
          if (e.key === 'ArrowRight' && gameState.current.velocity > 0) {
            gameState.current.velocity = 0;
          } else if (e.key === 'ArrowLeft' && gameState.current.velocity < 0) {
            gameState.current.velocity = 0;
          }
        };

        window.addEventListener('keydown', handleKeyDown);
        window.addEventListener('keyup', handleKeyUp);

        // Controles m贸viles
        if (isMobile) {
          const setupMobileControls = () => {
            const leftBtn = document.querySelector('.mobile-button.left');
            const rightBtn = document.querySelector('.mobile-button.right');
            const upBtn = document.querySelector('.mobile-button.up');

            if (leftBtn && rightBtn && upBtn) {
              leftBtn.addEventListener('touchstart', (e) => {
                e.preventDefault();
                gameState.current.velocity = -1;
                gameState.current.direction = 'left';
              });
              
              leftBtn.addEventListener('touchend', () => {
                if (gameState.current.velocity === -1) {
                  gameState.current.velocity = 0;
                }
              });

              rightBtn.addEventListener('touchstart', (e) => {
                e.preventDefault();
                gameState.current.velocity = 1;
                gameState.current.direction = 'right';
              });
              
              rightBtn.addEventListener('touchend', () => {
                if (gameState.current.velocity === 1) {
                  gameState.current.velocity = 0;
                }
              });

              upBtn.addEventListener('touchstart', (e) => {
                e.preventDefault();
                if (!gameState.current.isJumping) {
                  gameState.current.isJumping = true;
                  gameState.current.jumpVelocity = JUMP_FORCE;
                }
              });
            }
          };

          setTimeout(setupMobileControls, 100);
        }

        return () => {
          window.removeEventListener('keydown', handleKeyDown);
          window.removeEventListener('keyup', handleKeyUp);
          app.destroy(true);
        };
      } catch (error) {
        console.error('Error al cargar el juego:', error);
        setModalContent(`
          <h2>Error al cargar el juego</h2>
          <p>${error.message}</p>
          <p>Verifica que todos los assets est茅n en la carpeta /assets</p>
        `);
        setModalVisible(true);
        setLoading(false);
      }
    };

    initGame();

    return () => {
      if (appRef.current) appRef.current.destroy(true);
      if (audioRef.current) {
        audioRef.current.pause();
        audioRef.current = null;
      }
      document.body.style.overflow = '';
    };
  }, []);

  return (
    <div className="game-container">
      {loading && (
        <div className="loading-screen">
          <div className="loading-progress">
            <div className="progress-bar" style={{ width: `${loadProgress}%` }}></div>
            <div className="progress-text">Cargando... {loadProgress}%</div>
          </div>
        </div>
      )}

      <header className="header-juego">
        <h1>Mi Portfolio Interactivo</h1>
        <p>Desarrollador Full Stack | Especialista en React</p>
        
        <div className="redes">
          <a href="https://linkedin.com" target="_blank" rel="noopener noreferrer">
            <img src="/assets/linkedin.png" alt="LinkedIn" />
          </a>
          <a href="mailto:ejemplo@email.com">
            <img src="/assets/email.png" alt="Email" />
          </a>
          <a href="https://github.com" target="_blank" rel="noopener noreferrer">
            <img src="/assets/github.png" alt="GitHub" />
          </a>
          <button
            className="mute-button"
            onClick={() => {
              if (audioRef.current) {
                audioRef.current.muted = !audioRef.current.muted;
                setMuted(audioRef.current.muted);
              }
            }}
          >
            {muted ? '' : ''}
          </button>
        </div>
      </header>

      <div ref={containerRef} className="pixi-container" />

      {modalVisible && (
        <div className="modal">
          <div className="modal-content">
            <span onClick={() => setModalVisible(false)} className="close">&times;</span>
            <div dangerouslySetInnerHTML={{ __html: modalContent }} />
            <p className="modal-hint">(Presiona Escape para cerrar)</p>
          </div>
        </div>
      )}

      {isMobile && (
        <div className="mobile-controls">
          <button className="mobile-button left">猬锔</button>
          <button className="mobile-button up">猬锔</button>
          <button className="mobile-button right">★</button>
        </div>
      )}
    </div>
  );
};

export default Juego;
