import React, { useEffect, useRef, useState } from 'react';
import {
  Application, Assets, Sprite, Container, TilingSprite, Text, TextStyle
} from 'pixi.js';
import './Juego.css';

const Juego = () => {
  const [loading, setLoading] = useState(true);
  const [loadProgress, setLoadProgress] = useState(0);
  const [showHelp, setShowHelp] = useState(true);
  const containerRef = useRef(null);
  const [modalVisible, setModalVisible] = useState(false);
  const [modalContent, setModalContent] = useState('');
  const [muted, setMuted] = useState(false);
  const audioRef = useRef(null);
  const velocityRef = useRef(0);
  const jumpVelocityRef = useRef(0);
  const isJumpingRef = useRef(false);
  const directionRef = useRef('right');
  const appRef = useRef(null);
  const marioRef = useRef(null);
  const worldRef = useRef(null);
  const fondoRef = useRef(null);
  const bloquesRef = useRef([]);

  // Efecto para manejar la tecla Escape
  useEffect(() => {
    const handleEscape = (e) => {
      if (e.key === 'Escape' && modalVisible) {
        setModalVisible(false);
      }
    };

    window.addEventListener('keydown', handleEscape);
    return () => {
      window.removeEventListener('keydown', handleEscape);
    };
  }, [modalVisible]);

  useEffect(() => {
    // Deshabilitar scroll en la p√°gina
    document.body.style.overflow = 'hidden';
    document.documentElement.style.overflow = 'hidden';

    // üéµ M√∫sica
    audioRef.current = new Audio(process.env.PUBLIC_URL + '/assets/musica.mp3');
    audioRef.current.loop = true;
    audioRef.current.volume = 0.05;

    const tryPlay = () => {
      audioRef.current.play().catch(err => {
        console.warn('üîá Autoplay bloqueado, requiere interacci√≥n del usuario.');
      });
      document.removeEventListener('click', tryPlay);
    };
    tryPlay();
    document.addEventListener('click', tryPlay);

        const bloqueContenidos = [
      // Experiencia
      `<h2>Experiencia Profesional</h2>
       <h3>GRUPO DCI (Aug 2023 - Actual)</h3>
       <p><strong>Project Manager & Developer</strong></p>
       <ul>
         <li>Gesti√≥n de documentaci√≥n de proyectos (historias de usuario, requisitos funcionales)</li>
         <li>Colaboraci√≥n con stakeholders para priorizar requisitos</li>
         <li>Desarrollo de sistema de gesti√≥n de empleados con Dart, FlutterFlow y Firebase</li>
         <li>Dise√±o de base de datos Firebase para registros de entrada/salida</li>
       </ul>
       
       <h3>Align Technology</h3>
       <p><strong>CAD Designer</strong></p>
       <ul>
         <li>Dise√±o de modelos 3D CAD para procesos de producci√≥n</li>
         <li>Creaci√≥n de documentaci√≥n t√©cnica (planos, esquemas)</li>
         <li>Migraci√≥n de plataforma y capacitaci√≥n de 30+ empleados</li>
       </ul>`,

      // Educaci√≥n
      `<h2>Educaci√≥n</h2>
       <h3>Universidad Fidelitas</h3>
       <p><strong>Ingenier√≠a en Sistemas</strong> (Dic 2025)</p>
       
       <h3>VMEDU</h3>
       <p><strong>Scrum Fundamentals Certified</strong> (Mar 2023)</p>`,

      // Habilidades T√©cnicas
      `<h2>Habilidades T√©cnicas</h2>
       <div class="skills-columns">
         <div>
           <h3>Lenguajes</h3>
           <ul>
             <li>Dart</li>
             <li>Java</li>
             <li>C#</li>
             <li>PHP</li>
             <li>HTML/CSS</li>
           </ul>
         </div>
         <div>
           <h3>Frameworks</h3>
           <ul>
             <li>FlutterFlow</li>
             <li>ASP.Net</li>
             <li>Blazor</li>
             <li>React</li>
           </ul>
         </div>
         <div>
           <h3>Bases de Datos</h3>
           <ul>
             <li>Firebase</li>
             <li>MySQL</li>
             <li>Oracle</li>
           </ul>
         </div>
       </div>`,

      // Idiomas
      `<h2>Idiomas</h2>
       <ul>
         <li><strong>Espa√±ol:</strong> Nativo (C2)</li>
         <li><strong>Ingl√©s:</strong> Avanzado (B2)</li>
       </ul>`,

      // Contacto
      `<h2>Informaci√≥n de Contacto</h2>
       <ul class="contact-info">
         <li>üìß <strong>Email:</strong> emalva99guz@outlook.com</li>
         <li>üì± <strong>Tel√©fono:</strong> +506 7007 2655</li>
         <li>üìç <strong>Ubicaci√≥n:</strong> Cartago, Costa Rica</li>
         <li>üîó <strong>LinkedIn:</strong> <a href="https://www.linkedin.com/in/emmanuel-malavassi" target="_blank">linkedin.com/in/emmanuel-malavassi</a></li>
       </ul>`,

      // Resumen Profesional
      `<h2>Resumen Profesional</h2>
       <p>Estudiante y Desarrollador en GRUPO DCI con conocimientos en lenguajes de programaci√≥n y metodolog√≠as √°giles.</p>
       <p>Experiencia en gesti√≥n de documentaci√≥n de proyectos, colaboraci√≥n con stakeholders y desarrollo de sistemas que optimizan procesos empresariales.</p>
       <p>Habilidades comprobadas en resoluci√≥n de problemas, trabajo en equipo y an√°lisis de datos.</p>`,

      // Proyectos
      `<h2>Proyectos Destacados</h2>
       <h3>Sistema de Gesti√≥n de Empleados</h3>
       <ul>
         <li>Desarrollado con Dart, FlutterFlow y Firebase</li>
         <li>Optimizaci√≥n de registros de entrada/salida</li>
         <li>Mejora de m√©tricas de desempe√±o</li>
       </ul>
       
       <h3>Migraci√≥n de Plataforma</h3>
       <ul>
         <li>Colaboraci√≥n en proyecto de migraci√≥n</li>
         <li>Capacitaci√≥n de m√°s de 30 empleados</li>
         <li>Documentaci√≥n t√©cnica y soporte</li>
       </ul>`,

      // Herramientas
      `<h2>Herramientas</h2>
       <div class="tools-grid">
         <div><img src="/assets/figma.png" alt="Figma"> Figma</div>
         <div><img src="/assets/docker.png" alt="Docker"> Docker</div>
         <div><img src="/assets/github.png" alt="GitHub"> GitHub</div>
         <div><img src="/assets/vs.png" alt="VS"> Visual Studio</div>
         <div><img src="/assets/adobe.png" alt="Adobe XD"> Adobe XD</div>
         <div><img src="/assets/vm.png" alt="VMs"> Virtual Machines</div>
       </div>`,

      // Soft Skills
      `<h2>Habilidades Blandas</h2>
       <ul class="soft-skills">
         <li>‚úîÔ∏è Resoluci√≥n de problemas</li>
         <li>‚úîÔ∏è Metodolog√≠as √°giles</li>
         <li>‚úîÔ∏è Pensamiento anal√≠tico</li>
         <li>‚úîÔ∏è Gesti√≥n del tiempo</li>
         <li>‚úîÔ∏è Comunicaci√≥n efectiva</li>
         <li>‚úîÔ∏è Trabajo en equipo</li>
         <li>‚úîÔ∏è Creatividad</li>
         <li>‚úîÔ∏è Profesionalismo</li>
       </ul>`,

      // Extras
      `<h2>Experiencias Adicionales</h2>
       <h3>Biotech Lab</h3>
       <p><strong>Asistente de Laboratorio</strong></p>
       <ul>
         <li>Soporte t√©cnico para sistemas IT del laboratorio</li>
         <li>Gesti√≥n de producci√≥n de microorganismos</li>
       </ul>
       
       <h3>Arcos Dorados</h3>
       <p><strong>Asociado de Servicio al Cliente</strong></p>
       <ul>
         <li>Operaci√≥n de sistemas punto de venta</li>
         <li>Gesti√≥n de inventario</li>
       </ul>`
    ];
    const etiquetasBloques = [
      'Experiencia', 'Educaci√≥n', 'Habilidades', 'Idiomas', 'Contacto',
      'Resumen', 'Proyectos', 'Herramientas', 'Soft Skills', 'Extras',
    ];


    const cargarJuego = async () => {
      try {
        // 1. Crear la aplicaci√≥n PixiJS
        const app = new Application({
          width: window.innerWidth,
          height: window.innerHeight,
          backgroundColor: 0x87ceeb,
        });
        appRef.current = app;
        containerRef.current.appendChild(app.canvas);

        // 2. Configurar loader con progreso
        Assets.init({ basePath: process.env.PUBLIC_URL + '/assets' });

        // Lista de assets con alias
        const assetList = [
          { alias: 'bloque', src: 'bloque.png' },
          { alias: 'suelo', src: 'suelo.png' },
          { alias: 'nube', src: 'nube.png' },
          { alias: 'fondo', src: 'ciudad.png' },
          { alias: 'farol', src: 'farol.png' },
          { alias: 'arbusto', src: 'arbusto.png' },
          { alias: 'walk0', src: 'tile000.png' },
          { alias: 'walk1', src: 'tile001.png' },
          { alias: 'walk2', src: 'tile002.png' },
          { alias: 'walk3', src: 'tile003.png' },
          { alias: 'walk4', src: 'tile004.png' },
          { alias: 'walk5', src: 'tile005.png' },
          { alias: 'walk6', src: 'tile006.png' },
          { alias: 'walk7', src: 'tile007.png' },
        ];

        // 3. Cargar assets con seguimiento de progreso
        Assets.load(assetList, (progress) => {
          setLoadProgress(Math.round(progress * 100));
        })
        .then(() => {
          setLoading(false);
          inicializarJuego(app);
        })
        .catch(err => {
          console.error('Error cargando assets:', err);
          setModalContent(`
            <h2>Error al cargar recursos</h2>
            <p>No se pudieron cargar las im√°genes del juego.</p>
            <p>Verifica que todos los archivos est√©n en la carpeta public/assets/</p>
            <p>Error: ${err.message}</p>
          `);
          setModalVisible(true);
        });

        const inicializarJuego = (app) => {
          // Obtener texturas
          const bloqueTexture = Assets.get('bloque');
          const sueloTexture = Assets.get('suelo');
          const nubeTexture = Assets.get('nube');
          const fondoTexture = Assets.get('fondo');
          const farolTexture = Assets.get('farol');
          const arbustoTexture = Assets.get('arbusto');

          const framesLeft = [
            Assets.get('walk0'), Assets.get('walk1'), 
            Assets.get('walk2'), Assets.get('walk3')
          ];
          const framesRight = [
            Assets.get('walk7'), Assets.get('walk6'), 
            Assets.get('walk5'), Assets.get('walk4')
          ];

          // Configurar fondo
          const fondo = new TilingSprite(fondoTexture, 6000, app.screen.height);
          fondo.zIndex = -1;
          fondoRef.current = fondo;
          app.stage.addChild(fondo);

          // Crear mundo
          const world = new Container();
          worldRef.current = world;
          app.stage.addChild(world);

          // üå•Ô∏è Bienvenida en nube
          const nubeInicio = new Sprite(nubeTexture);
          nubeInicio.x = 200;
          nubeInicio.y = 200;
          nubeInicio.scale.set(0.8);
          nubeInicio.alpha = 0.9;
          world.addChild(nubeInicio);

          const bienvenidaText = new Text(
            '¬°Bienvenido!\nUsa flechas para moverte\nEscape para cerrar modales',
            new TextStyle({
              fontFamily: 'Arial',
              fontSize: 16,
              fill: 'black',
              align: 'center',
              wordWrap: true,
              wordWrapWidth: 200,
            })
          );
          bienvenidaText.anchor.set(0.5);
          bienvenidaText.x = nubeInicio.x + nubeInicio.width / 2;
          bienvenidaText.y = nubeInicio.y + nubeInicio.height / 2;
          world.addChild(bienvenidaText);

          // Nubes din√°micas
          for (let i = 0; i < 10; i++) {
            const nube = new Sprite(nubeTexture);
            nube.x = i * 800;
            nube.y = 80 + Math.random() * 20;
            nube.alpha = 0.9;
            nube.scale.set(0.5);
            world.addChild(nube);
          }

          // Suelo
          for (let i = 0; i < 100; i++) {
            const suelo = new Sprite(sueloTexture);
            suelo.scale.set(0.4);
            suelo.x = i * (suelo.width * 0.3);
            suelo.y = 750;
            world.addChild(suelo);
          }

          // Bloques interactivos
          const bloques = [];
          bloquesRef.current = bloques;
          for (let i = 0; i < 10; i++) {
            const bloque = new Sprite(bloqueTexture);
            bloque.scale.set(0.05);
            bloque.y = 470;
            bloque.x = 900 + i * 400;
            bloque.eventMode = 'static';
            bloque.cursor = 'pointer';
            bloque.bloqueID = i + 1;
            world.addChild(bloque);
            bloques.push(bloque);

            const textoBurbuja = new Text(etiquetasBloques[i], new TextStyle({
              fontFamily: 'Arial',
              fontSize: 18,
              fill: 'white',
              stroke: '#000',
              strokeThickness: 3,
              align: 'center',
            }));
            textoBurbuja.anchor.set(0.5);
            textoBurbuja.x = bloque.x + (bloque.width * 0.5);
            textoBurbuja.y = bloque.y - 40;
            world.addChild(textoBurbuja);
          }

          // Elementos decorativos
          for (let i = 0; i < 5; i++) {
            const farol = new Sprite(farolTexture);
            farol.scale.set(0.58);
            farol.x = 600 + i * 800;
            farol.y = 433;
            world.addChild(farol);
          }

          for (let i = 0; i < 8; i++) {
            const arbusto = new Sprite(arbustoTexture);
            arbusto.x = 300 + i * 600;
            arbusto.y = 495;
            world.addChild(arbusto);
          }

          // Personaje
          const mario = new Sprite(framesRight[0]);
          mario.scale.set(0.58);
          mario.x = app.screen.width / 4;
          mario.y = 750;
          mario.anchor.set(0.5, 1);
          marioRef.current = mario;
          app.stage.addChild(mario);

          let currentFrame = 0;
          let frameCounter = 0;
          const gravity = 1;
          const floorY = 750;

          const moverMundo = () => {
            // (Mant√©n la misma l√≥gica de movimiento que ten√≠as)
          };

          app.ticker.add(moverMundo);

          const handleKeyDown = (e) => {
            // (Mant√©n los mismos controles de teclado)
          };

          const handleKeyUp = () => {
            if (!modalVisible) velocityRef.current = 0;
          };

          window.addEventListener('keydown', handleKeyDown);
          window.addEventListener('keyup', handleKeyUp);

          const preventTouch = (e) => e.preventDefault();
          document.addEventListener('touchmove', preventTouch, { passive: false });

          return () => {
            window.removeEventListener('keydown', handleKeyDown);
            window.removeEventListener('keyup', handleKeyUp);
            document.removeEventListener('touchmove', preventTouch);
            app.destroy(true, true);
            document.body.style.overflow = '';
            document.documentElement.style.overflow = '';
          };
        };
      } catch (err) {
        console.error('Error inicializando el juego:', err);
        setModalContent(`
          <h2>Error cr√≠tico</h2>
          <p>No se pudo inicializar el juego.</p>
          <p>${err.message}</p>
        `);
        setModalVisible(true);
      }
    };

    cargarJuego();

    return () => {
      if (appRef.current) {
        appRef.current.destroy(true, true);
      }
      document.body.style.overflow = '';
      document.documentElement.style.overflow = '';
    };
  }, []);

  const [isMobile, setIsMobile] = useState(false);

  useEffect(() => {
    const checkMobile = () => {
      setIsMobile(window.innerWidth <= 768);
    };
    checkMobile();
    window.addEventListener('resize', checkMobile);
    return () => window.removeEventListener('resize', checkMobile);
  }, []);

  return (
    <div className="game-container">
      {loading && (
        <div className="loading-screen">
          <div className="loading-progress">
            <div className="progress-bar" style={{ width: `${loadProgress}%` }}></div>
            <div className="progress-text">Cargando... {loadProgress}%</div>
          </div>
        </div>
      )}

      <header className="header-juego">
        <h1>Emanuel Alvarado Guzman</h1>
        <p>Desarrollador Full Stack | Analista de datos</p>

        <div className="redes">
          <a href="https://www.linkedin.com/in/emanuel-isaac-alvarado-guzman-2a30b2263/" target="_blank" rel="noopener noreferrer">
            <img src={process.env.PUBLIC_URL + "/assets/linkedin.png"} alt="LinkedIn" />
          </a>
          <a href="mailto:emalva99guz@outlook.com">
            <img src={process.env.PUBLIC_URL + "/assets/email.png"} alt="Email" />
          </a>
          <a href="https://github.com/hdecache99" target="_blank" rel="noopener noreferrer">
            <img src={process.env.PUBLIC_URL + "/assets/github.png"} alt="GitHub" />
          </a>
          <button
            className="mute-button"
            onClick={() => {
              const current = audioRef.current;
              current.muted = !current.muted;
              setMuted(current.muted);
            }}
          >
            {muted ? 'üîá' : 'üîä'}
          </button>
        </div>
      </header>

      <div ref={containerRef} className="pixi-container" />

      {modalVisible && (
        <div className="modal">
          <div className="modal-content">
            <span onClick={() => setModalVisible(false)} className="close">&times;</span>
            <div dangerouslySetInnerHTML={{ __html: modalContent }} />
            <p className="modal-hint">(Presiona Escape para cerrar)</p>
          </div>
        </div>
      )}

      {isMobile && (
        <div className="mobile-controls">
          <button
            className="mobile-button left"
            onTouchStart={(e) => { 
              e.preventDefault();
              velocityRef.current = -4;
              directionRef.current = 'left';
            }}
            onTouchEnd={(e) => { 
              e.preventDefault();
              velocityRef.current = 0; 
            }}
          >
            ‚¨ÖÔ∏è
          </button>
          <button
            className="mobile-button up"
            onTouchStart={(e) => {
              e.preventDefault();
              if (!isJumpingRef.current) {
                isJumpingRef.current = true;
                jumpVelocityRef.current = -20;
              }
            }}
          >
            ‚¨ÜÔ∏è
          </button>
          <button
            className="mobile-button right"
            onTouchStart={(e) => { 
              e.preventDefault();
              velocityRef.current = 4;
              directionRef.current = 'right';
            }}
            onTouchEnd={(e) => { 
              e.preventDefault();
              velocityRef.current = 0; 
            }}
          >
            ‚û°Ô∏è
          </button>
        </div>
      )}
    </div>
  );
};

export default Juego;
